   846→  padding: var(--spacing-2) var(--spacing-4);
   847→  background: var(--color-primary-500);
   848→  color: white;
   849→  text-decoration: none;
   850→  border-radius: var(--radius-base);
   851→  font-weight: var(--font-weight-medium);
   852→}
   853→
   854→.skip-link:focus {
   855→  position: fixed !important;
   856→  left: var(--spacing-2) !important;
   857→  top: var(--spacing-2) !important;
   858→  width: auto !important;
   859→  height: auto !important;
   860→  overflow: visible !important;
   861→}
   862→
   863→/* Real-time connection indicators */
   864→.connection-indicator {
   865→  display: inline-flex;
   866→  align-items: center;
   867→  gap: var(--spacing-2);
   868→  padding: var(--spacing-1) var(--spacing-2);
   869→  border-radius: var(--radius-full);
   870→  font-size: var(--font-size-xs);
   871→  font-weight: var(--font-weight-medium);
   872→}
   873→
   874→.connection-indicator--connected {
   875→  background: var(--color-success-100);
   876→  color: var(--color-success-700);
   877→  border: 1px solid var(--color-success-200);
   878→}
   879→
   880→.connection-indicator--connecting {
   881→  background: var(--color-warning-100);
   882→  color: var(--color-warning-700);
   883→  border: 1px solid var(--color-warning-200);
   884→}
   885→
   886→.connection-indicator--disconnected {
   887→  background: var(--color-error-100);
   888→  color: var(--color-error-700);
   889→  border: 1px solid var(--color-error-200);
   890→}
   891→
   892→[data-theme="dark"] .connection-indicator--connected {
   893→  background: var(--color-success-950);
   894→  color: var(--color-success-300);
   895→  border: 1px solid var(--color-success-800);
   896→}
   897→
   898→[data-theme="dark"] .connection-indicator--connecting {
   899→  background: var(--color-warning-950);
   900→  color: var(--color-warning-300);
   901→  border: 1px solid var(--color-warning-800);
   902→}
   903→
   904→[data-theme="dark"] .connection-indicator--disconnected {
   905→  background: var(--color-error-950);
   906→  color: var(--color-error-300);
   907→  border: 1px solid var(--color-error-800);
   908→}
   909→
   910→/* Notification badges */
   911→.notification-badge {
   912→  display: inline-flex;
   913→  align-items: center;
   914→  justify-content: center;
   915→  min-width: 1.25rem;
   916→  height: 1.25rem;
   917→  padding: 0 0.25rem;
   918→  font-size: 0.75rem;
   919→  font-weight: var(--font-weight-bold);
   920→  line-height: 1;
   921→  border-radius: var(--radius-full);
   922→  white-space: nowrap;
   923→}
   924→
   925→.notification-badge--primary {
   926→  background: var(--color-primary-500);
   927→  color: white;
   928→}
   929→
   930→.notification-badge--error {
   931→  background: var(--color-error-500);
   932→  color: white;
   933→}
   934→
   935→.notification-badge--warning {
   936→  background: var(--color-warning-500);
   937→  color: white;
   938→}
   939→
   940→.notification-badge--success {
   941→  background: var(--color-success-500);
   942→  color: white;
   943→}
   944→
   945→/* Performance optimizations */
   946→.will-change-transform {
   947→  will-change: transform;
   948→}
   949→
   950→.will-change-opacity {
   951→  will-change: opacity;
   952→}
   953→
   954→.gpu-acceleration {
   955→  transform: translateZ(0);
   956→  backface-visibility: hidden;
   957→  perspective: 1000;
   958→}
   959→
   960→/* Battery optimization styles */
   961→@media (max-width: 768px) {
   962→  .battery-optimize {
   963→    animation-duration: 0s !important;
   964→    transition-duration: 0.1s !important;
   965→  }
   966→  
   967→  .battery-optimize .glass-medium,
   968→  .battery-optimize .glass-strong {
   969→    backdrop-filter: none !important;
   970→    -webkit-backdrop-filter: none !important;
   971→    background: var(--color-surface-primary) !important;
   972→  }
   973→}
   974→
   975→/* Print styles */
   976→@media print {
   977→  .no-print {
   978→    display: none !important;
   979→  }
   980→  
   981→  .notifications-container,
   982→  .chat-widget-container,
   983→  .connection-indicator {
   984→    display: none !important;
   985→  }
   986→  
   987→  * {
   988→    background: transparent !important;
   989→    color: black !important;
   990→    box-shadow: none !important;
   991→    text-shadow: none !important;
   992→  }
   993→}
   994→
   995→/* ===== MOBILE OPTIMIZATIONS ===== */
   996→
   997→/* Touch optimization styles */
   998→.touch-optimized {
   999→  -webkit-tap-highlight-color: transparent;
  1000→  -webkit-touch-callout: none;
  1001→  -webkit-user-select: none;
  1002→  user-select: none;
  1003→}
  1004→
  1005→.touch-optimized button,
  1006→.touch-optimized [role="button"],
  1007→.touch-optimized a,
  1008→.touch-optimized input,
  1009→.touch-optimized select,
  1010→.touch-optimized textarea {
  1011→  -webkit-user-select: text;
  1012→  user-select: text;
  1013→}
  1014→
  1015→/* Large touch targets for mobile */
  1016→.large-touch-targets button,
  1017→.large-touch-targets [role="button"],
  1018→.large-touch-targets a,
  1019→.large-touch-targets .btn-base {
  1020→  min-height: 44px;
  1021→  min-width: 44px;
  1022→  padding: var(--spacing-3) var(--spacing-4);
  1023→}
  1024→
  1025→.large-touch-targets .btn-sm {
  1026→  min-height: 40px;
  1027→  min-width: 40px;
  1028→}
  1029→
  1030→.large-touch-targets .btn-lg {
  1031→  min-height: 48px;
  1032→  min-width: 48px;
  1033→}
  1034→
  1035→/* Performance mode optimizations */
  1036→.performance-mode * {
  1037→  will-change: auto !important;
  1038→  animation-duration: 0.1s !important;
  1039→  transition-duration: 0.1s !important;
  1040→}
  1041→
  1042→.performance-mode .glass-medium,
