  1045→  -webkit-backdrop-filter: none !important;
  1046→  background: var(--color-surface-primary) !important;
  1047→}
  1048→
  1049→/* Reduced animations for accessibility and battery */
  1050→.reduce-animations * {
  1051→  animation-duration: 0.01ms !important;
  1052→  animation-iteration-count: 1 !important;
  1053→  transition-duration: 0.01ms !important;
