  1055→
  1056→.reduce-animations .animate-pulse,
  1057→.reduce-animations .animate-bounce,
  1058→.reduce-animations .animate-spin {
  1059→  animation: none !important;
  1060→}
  1061→
  1062→/* Mobile-specific breakpoints */
  1063→@media (max-width: 480px) {
  1064→  /* Extra small mobile phones */
  1065→  .responsive-mobile-xs {
  1066→    font-size: 14px;
  1067→    padding: var(--spacing-2);
  1068→  }
  1069→  
  1070→  .btn-base {
  1071→    font-size: var(--font-size-sm);
  1072→    padding: var(--spacing-2) var(--spacing-3);
  1073→  }
  1074→  
  1075→  .modal-backdrop {
  1076→    backdrop-filter: none;
  1077→    -webkit-backdrop-filter: none;
  1078→  }
  1079→}
  1080→
  1081→@media (max-width: 640px) {
