  1144→}
  1145→
  1146→/* Pointer device optimization */
  1147→@media (pointer: coarse) {
  1148→  /* Touch devices */
  1149→  .form-control-base {
  1150→    font-size: 16px; /* Prevents zoom on iOS */
  1151→    padding: var(--spacing-3);
  1152→  }
  1153→  
  1154→  .btn-base {
  1155→    padding: var(--spacing-3) var(--spacing-4);
  1156→  }
  1157→  
  1158→  select,
  1159→  input,
  1160→  textarea {
  1161→    font-size: 16px; /* Prevents zoom on iOS */
  1162→  }
  1163→}
  1164→
  1165→/* Safe area insets for devices with notches */
  1166→@supports (padding: max(0px)) {
  1167→  .safe-area-inset-top {
  1168→    padding-top: max(var(--spacing-4), env(safe-area-inset-top));
  1169→  }
  1170→  
  1171→  .safe-area-inset-bottom {
