  1173→  }
  1174→  
  1175→  .safe-area-inset-left {
  1176→    padding-left: max(var(--spacing-4), env(safe-area-inset-left));
  1177→  }
  1178→  
  1179→  .safe-area-inset-right {
  1180→    padding-right: max(var(--spacing-4), env(safe-area-inset-right));
  1181→  }
  1182→}
  1183→
  1184→/* PWA display modes */
  1185→@media (display-mode: standalone) {
  1186→  .pwa-standalone {
  1187→    padding-top: var(--spacing-4);
  1188→  }
  1189→  
  1190→  .pwa-standalone .navbar {
