  1216→}
  1217→
  1218→.gesture-feedback::after {
  1219→  content: '';
  1220→  position: absolute;
  1221→  top: 50%;
  1222→  left: 50%;
  1223→  width: 0;
  1224→  height: 0;
  1225→  border-radius: 50%;
  1226→  background: rgba(0, 0, 0, 0.1);
