  1267→  height: 0;
  1268→  border-left: 6px solid var(--color-gray-400);
  1269→  border-top: 4px solid transparent;
  1270→  border-bottom: 4px solid transparent;
  1271→  transform: translateY(-50%);
  1272→  opacity: 0.5;
  1273→}
  1274→
  1275→/* Loading states optimized for mobile */
  1276→.mobile-loading {
  1277→  display: flex;
  1278→  flex-direction: column;
  1279→  align-items: center;
  1280→  justify-content: center;
