--ease-out: cubic-bezier(0, 0, 0.2, 1);          /* Most common, decelerating */
--ease-in-out: cubic-bezier(0.4, 0, 0.2, 1);     /* Symmetrical */
--ease-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55);  /* Playful overshoot */
