     6→      <span v-if="required" class="text-error-500 font-medium">*</span>
     7→    </label>
     8→    <div class="relative flex items-center">
     9→      <Icon v-if="icon && mounted" :icon="icon" class="absolute left-3 text-gray-400 dark:text-gray-500 w-5 h-5 transition-colors duration-base pointer-events-none" />
    10→      <input :value="modelValue" @input="handleInput" @blur="handleBlur" :class="[
    11→        'w-full py-3 px-4 h-12 border border-gray-200 dark:border-gray-700 rounded-lg',
    12→        'bg-white dark:bg-gray-800 text-gray-900 dark:text-white',
    13→        'transition-all duration-base focus-ring focus:border-primary-500',
    14→        'disabled:opacity-60 disabled:cursor-not-allowed disabled:bg-gray-100 dark:disabled:bg-gray-700',
    15→        props.class ?? '',
    16→        inputClass,
    17→        icon ? 'pl-10' : '',
    18→      ]" :type="type || 'text'" :placeholder="placeholder" :required="required" />
    19→    </div>
    20→    <p v-if="errorMessage" class="mt-2 text-sm text-error-600 dark:text-error-400 animate-fade-in">
    21→      {{ errorMessage }}
    22→    </p>
    23→  </div>
    24→</template>
    25→<script setup lang="ts">
    26→import { computed, ref } from "vue";
    27→import { useMounted } from "@vueuse/core";
    28→import { Icon } from "@iconify/vue";
    29→
    30→const props = defineProps<{
    31→  isValid?: number;
    32→  isValidTest?: (value: string) => boolean;
    33→  invalidMessage?: string;
    34→  type?: string;
    35→  placeholder?: string;
    36→  icon?: string;
    37→  label?: string;
    38→  class?: string;
    39→  required?: boolean;
    40→}>();
    41→
    42→const modelValue = defineModel<string>("modelValue", {
    43→  default: "",
    44→});
    45→
    46→const mounted = useMounted();
    47→const hasBlurredInput = ref(false);
    48→const errorMessage = ref("");
    49→
    50→const inputClass = computed(() => {
    51→  if (!mounted.value) return "";
    52→  if (!hasBlurredInput.value) return "";
    53→  if (props.isValidTest) {
    54→    return props.isValidTest(modelValue.value) || props.isValid
    55→      ? "success !border-success-500 focus:!shadow-focus-success"
    56→      : "error !border-error-500 focus:!shadow-focus-error";
    57→  }
    58→  if (props.required && !modelValue.value) {
    59→    return "error !border-error-500 focus:!shadow-focus-error";
    60→  }
    61→  if (props.isValid === undefined) return "";
    62→  return props.isValid 
    63→    ? "success !border-success-500 focus:!shadow-focus-success" 
    64→    : "error !border-error-500 focus:!shadow-focus-error";
    65→});
    66→
    67→const handleInput = (event: Event) => {
    68→  const target = event.target as HTMLInputElement;
    69→  modelValue.value = target.value;
    70→  // Only clear error messages when typing if field becomes valid
    71→  if (hasBlurredInput.value && errorMessage.value) {
    72→    if (props.isValidTest && props.isValidTest(target.value)) {
    73→      errorMessage.value = "";
    74→    } else if (props.required && target.value.trim()) {
    75→      errorMessage.value = "";
    76→    }
    77→  }
    78→};
    79→
    80→const handleBlur = () => {
    81→  hasBlurredInput.value = true;
    82→  validateInput();
    83→};
    84→
    85→const validateInput = () => {
    86→  errorMessage.value = "";
    87→  if (props.required && !modelValue.value) {
    88→    errorMessage.value = "This field is required";
    89→  } else if (props.isValidTest && !props.isValidTest(modelValue.value)) {
    90→    errorMessage.value = props.invalidMessage || "Invalid input";
    91→  }
    92→};
    93→</script>

