    54→<script setup lang="ts">
    55→import { ref, computed, watch, nextTick, onMounted } from "vue";
    56→import { useMounted, onClickOutside, useWindowSize, useWindowScroll } from "@vueuse/core";
    57→import { useFloating, offset, flip, shift } from "@floating-ui/vue";
    58→import { Icon } from "@iconify/vue";
    59→
    60→interface Option {
    61→  value: string;
    62→  label: string;
    63→}
    64→
    65→const props = defineProps<{
    66→  options: Option[];
    67→  isValidTest?: (value: string | string[]) => boolean;
    68→  isValid?: number;
    69→  placeholder?: string;
    70→  icon?: string;
    71→  label?: string;
    72→  description?: string;
    73→  multiple?: boolean;
    74→  disabled?: boolean;
    75→  required?: boolean;
    76→  invalidMessage?: string;
    77→  inputClass?: string;
    78→  defaultValue?: string;
    79→  /**
    80→   * Special option handling (like 'all' being exclusive with other options)
    81→   */
    82→  exclusiveOption?: string;
    83→}>();
    84→
    85→const modelValue = defineModel<string | string[]>({
    86→  default: undefined,
    87→});
    88→
    89→const mounted = useMounted();
    90→const hasBlurredInput = ref(false);
    91→const errorMessage = ref("");
    92→const isOpen = ref(false);
    93→const referenceEl = ref<HTMLElement>();
    94→const floatingEl = ref<HTMLElement>();
    95→
    96→const allOptions = computed(() => props.options);
    97→
    98→const selectedOption = computed(() => {
    99→  if (!modelValue.value || (Array.isArray(modelValue.value) && modelValue.value.length === 0)) {
   100→    return undefined;
   101→  }
   102→
   103→  return allOptions.value.find((option) =>
   104→    Array.isArray(modelValue.value)
   105→      ? modelValue.value[0] === option.value
   106→      : modelValue.value === option.value
   107→  );
   108→});
   109→
   110→const { x, y, strategy, update } = useFloating(referenceEl, floatingEl, {
   111→  placement: "bottom-start",
   112→  middleware: [offset(5), flip(), shift()],
   113→});
   114→
   115→const floatingStyles = computed(() => ({
   116→  position: strategy.value,
   117→  top: `${y.value}px`,
   118→  left: `${x.value}px`,
   119→  width: referenceEl.value ? `${referenceEl.value.offsetWidth}px` : "auto",
   120→}));
   121→
   122→const inputClass = computed(() => {
   123→  if (!mounted.value) return "";
   124→  if (!hasBlurredInput.value) return "";
   125→
   126→  if (props.required &&
   127→    (!modelValue.value || (Array.isArray(modelValue.value) && modelValue.value.length === 0))) {
   128→    return "error !border-error-500 focus:!shadow-focus-error";
   129→  }
   130→
   131→  if (props.isValidTest) {
   132→    return props.isValidTest(modelValue.value) || props.isValid
   133→      ? "success !border-success-500 focus:!shadow-focus-success"
   134→      : "error !border-error-500 focus:!shadow-focus-error";
   135→  }
   136→
   137→  if (props.isValid === undefined) return "";
   138→  return props.isValid 
   139→    ? "success !border-success-500 focus:!shadow-focus-success" 
   140→    : "error !border-error-500 focus:!shadow-focus-error";
   141→});
   142→
   143→const toggleDropdown = () => {
   144→  if (props.disabled) return;
   145→  isOpen.value = !isOpen.value;
   146→  if (isOpen.value) {
   147→    nextTick(update);
   148→  }
   149→};
   150→
   151→const selectOption = (option: Option) => {
   152→  if (props.multiple && Array.isArray(modelValue.value)) {
   153→    // Handle special case for exclusive options like 'all'
   154→    if (props.exclusiveOption) {
   155→      if (option.value === props.exclusiveOption) {
   156→        // When 'all' is selected, deselect all other options
   157→        modelValue.value = [props.exclusiveOption];
   158→      } else {
   159→        // When a specific option is selected:
   160→        // 1. Remove the 'all' option if it exists
   161→        // 2. Toggle the selected option
   162→        const newValue = modelValue.value.filter(v => v !== props.exclusiveOption);
   163→        const index = newValue.indexOf(option.value);
   164→
   165→        if (index === -1) {
   166→          // Add the option if not already selected
   167→          newValue.push(option.value);
   168→          modelValue.value = newValue;
   169→        } else {
   170→          // Remove the option if already selected
   171→          newValue.splice(index, 1);
   172→          // If nothing left, default to 'all'
   173→          modelValue.value = newValue.length === 0 ? [props.exclusiveOption] : newValue;
   174→        }
   175→      }
   176→    } else {
   177→      // Standard multiple selection behavior without exclusivity
   178→      const index = modelValue.value.indexOf(option.value);
   179→      if (index === -1) {
   180→        modelValue.value = [...(modelValue.value || []), option.value];
   181→      } else {
   182→        modelValue.value = modelValue.value.filter((v) => v !== option.value);
   183→      }
   184→    }
   185→  } else {
   186→    modelValue.value = option.value;
   187→    isOpen.value = false;
   188→  }
   189→
   190→  hasBlurredInput.value = true;
   191→  nextTick(() => {
   192→    if (!props.multiple) {
   193→      isOpen.value = false;
   194→    }
   195→    validateInput();
   196→  });
   197→};
   198→
   199→const isSelected = (value: string) => {
   200→  return Array.isArray(modelValue.value)
   201→    ? modelValue.value.includes(value)
   202→    : modelValue.value === value;
   203→};
   204→
   205→const getOptionLabel = (value: string) => {
   206→  const option = props.options.find((opt) => opt.value === value);
   207→  return option ? option.label : value;
   208→};
   209→
   210→const handleBlur = () => {
   211→  hasBlurredInput.value = true;
   212→  if (!isOpen.value) {
   213→    nextTick(validateInput);
   214→  }
   215→};
   216→
   217→const validateInput = () => {
   218→  if (props.isValidTest === undefined) return;
   219→  errorMessage.value = "";
   220→
   221→  if (props.required &&
   222→    (!modelValue.value || (Array.isArray(modelValue.value) && modelValue.value.length === 0))) {
   223→    errorMessage.value = "This field is required";
   224→  } else if (props.isValidTest && !props.isValidTest(modelValue.value)) {
   225→    errorMessage.value = props.invalidMessage || "Invalid input";
   226→  }
   227→};
   228→
   229→onClickOutside(referenceEl, () => {
   230→  isOpen.value = false;
   231→});
   232→
   233→const { width, height } = useWindowSize();
   234→const { x: scrollX, y: scrollY } = useWindowScroll();
   235→
   236→watch([width, height, scrollX, scrollY], () => {
   237→  if (isOpen.value) {
   238→    update();
   239→  }
   240→});
   241→</script>
