   148→<script setup lang="ts">
   149→import { ref, computed, onMounted } from "vue";
   150→import { Icon } from "@iconify/vue";
   151→import PricingCard from "@vue/cards/PricingCard.vue";
   152→import EnterpriseCard from "@vue/cards/EnterpriseCard.vue";
   153→import ContactUsModal from "@vue/modals/popups/ContactUsModal.vue";
   154→import { YEARLY_DISCOUNT } from "@/constants/billingConstants";
   155→import { useBilling } from "@/components/composables/useBilling";
   156→import type { UnifiedPricingTier } from "@/types/pricing";
   157→
   158→// Use the unified billing composable
   159→const { 
   160→  loadPricingTiers, 
   161→  unifiedPricingTiers, 
   162→  error: billingError 
   163→} = useBilling();
   164→
   165→// Define the transformed tier type for PricingCard component
   166→type TransformedTier = {
   167→  name: string;
   168→  description: string;
   169→  monthlyPrice: number;
   170→  yearlyPrice: number;
   171→  features: string[];
   172→  limits: any;
   173→  billingKey: string;
   174→  buttonText: string;
   175→  isPopular: boolean;
   176→};
   177→
   178→// Billing state
   179→const isYearly = ref(false);
   180→
   181→// Contact modal state
   182→const showContactModal = ref(false);
   183→
   184→// Local loading/error state
   185→const isLoading = ref(true);
   186→const error = ref<string | null>(null);
   187→
   188→const yearlyDiscountPercentage = computed(() => {
   189→  return Math.round((1 - YEARLY_DISCOUNT) * 100);
   190→});
   191→
   192→// Transform unified pricing tier to the format expected by PricingCard
   193→const transformUnifiedTier = (tier: any): TransformedTier => {
   194→  console.log("Transforming tier:", tier);
   195→  // Generate features list from tier attributes
   196→  const features: string[] = [];
   197→  
   198→  // Connections
   199→  if (tier.connectionsMax === null) {
   200→    features.push("Unlimited Connected Accounts");
   201→  } else {
   202→    features.push(`Up to ${tier.connectionsMax} Connected Accounts`);
   203→  }
   204→  
   205→  // Teams
   206→  features.push(`${tier.teamsMax} Team${tier.teamsMax > 1 ? 's' : ''}`);
   207→  
   208→  // Users
   209→  features.push(`${tier.usersMax} User${tier.usersMax > 1 ? 's' : ''}`);
   210→  
   211→  // Storage
   212→  if (tier.storageMax >= 1024) {
   213→    const gb = Math.floor(tier.storageMax / 1024);
   214→    const remaining = tier.storageMax % 1024;
   215→    if (remaining === 0) {
   216→      features.push(`${gb} GB Storage Limit`);
   217→    } else {
   218→      features.push(`${gb}.${Math.round(remaining / 102.4)} GB Storage Limit`);
   219→    }
   220→  } else {
   221→    features.push(`${tier.storageMax} MB Storage Limit`);
   222→  }
   223→  
   224→  // AI Credits
   225→  if (tier.aiCreditsMax > 0) {
   226→    features.push(`${tier.aiCreditsMax} AI Credits`);
   227→  }
   228→  
   229→  // Executions
   230→  if (tier.unlimitedExecutions) {
   231→    features.push("Unlimited Workflow Executions");
   232→  }
   233→  
   234→  // Additional features for higher tiers
   235→  if (tier.billingKey !== 'free') {
   236→    features.push("Basic Analytics");
   237→    features.push("24/7 Automated Support");
   238→  }
   239→  
   240→  if (tier.billingKey === 'creator' || tier.billingKey === 'agency' || tier.billingKey === 'enterprise') {
   241→    features.push("Priority Support");
   242→  }
   243→  
   244→  if (tier.billingKey === 'agency' || tier.billingKey === 'enterprise') {
   245→    features.push("Advanced Analytics");
   246→  }
   247→  
   248→  // Generate limits object for backwards compatibility
   249→  const limits = {
   250→    connections: tier.connectionsMax,
   251→    teams: tier.teamsMax,
   252→    users: tier.usersMax,
   253→    storage: tier.storageMax,
   254→    aiCredits: tier.aiCreditsMax,
   255→    chatEnabled: tier.chatEnabled,
   256→    encodingEnabled: tier.encodingEnabled,
   257→    unlimited: tier.unlimitedExecutions,
   258→  };
   259→
   260→  return {
   261→    name: tier.name,
   262→    description: tier.description,
   263→    monthlyPrice: tier.monthlyPrice,
   264→    yearlyPrice: tier.yearlyPrice,
   265→    features: features,
   266→    limits: limits,
   267→    billingKey: tier.billingKey,
   268→    buttonText: tier.buttonText || "Get Started",
   269→    isPopular: tier.isPopular || false,
   270→  };
   271→};
   272→
   273→// Computed pricing data from unified pricing tiers
   274→const pricingData = computed(() => {
   275→  console.log("pricingData computed - unifiedPricingTiers.value:", unifiedPricingTiers.value);
   276→  
   277→  if (!unifiedPricingTiers.value || unifiedPricingTiers.value.length === 0) {
   278→    console.warn("No unified pricing tiers available");
   279→    return {
   280→      title: "Simple, Transparent Pricing",
   281→      subtitle: "Start free, upgrade as you grow. No hidden fees, cancel anytime.",
   282→      tiers: [] as TransformedTier[],
   283→    };
   284→  }
   285→  
   286→  const transformedTiers: TransformedTier[] = unifiedPricingTiers.value.map(transformUnifiedTier);
   287→  console.log("Transformed tiers:", transformedTiers);
   288→  
   289→  return {
   290→    title: "Simple, Transparent Pricing",
   291→    subtitle: "Start free, upgrade as you grow. No hidden fees, cancel anytime.",
   292→    tiers: transformedTiers,
   293→  };
   294→});
   295→
   296→// Fetch pricing tiers using the composable
   297→const fetchPricingTiers = async () => {
   298→  try {
   299→    isLoading.value = true;
   300→    error.value = null;
   301→    
   302→    console.log("Starting to load pricing tiers...");
   303→    // Explicitly request only visible tiers
   304→    const tiers = await loadPricingTiers(true);
   305→    console.log("Loaded pricing tiers (should be filtered):", tiers);
   306→    
   307→    // Client-side filtering as backup to ensure only visible tiers are shown
   308→    const visibleTiers = tiers.filter(tier => {
   309→      // Check if isVisibleOnHomepage is not explicitly false
   310→      const isVisible = tier.isVisibleOnHomepage !== false;
   311→      if (!isVisible) {
   312→        console.log(`Filtering out tier "${tier.name}" - isVisibleOnHomepage: ${tier.isVisibleOnHomepage}`);
   313→      }
   314→      return isVisible;
   315→    });
   316→    
   317→    console.log("Final visible tiers after client-side filtering:", visibleTiers);
   318→    console.log("unifiedPricingTiers.value:", unifiedPricingTiers.value);
   319→    
   320→    if (billingError.value) {
   321→      console.error("Billing error:", billingError.value);
   322→      error.value = billingError.value;
   323→    }
   324→    
   325→    if (!visibleTiers || visibleTiers.length === 0) {
   326→      console.warn("No visible pricing tiers available");
   327→      error.value = "No pricing plans available";
   328→    }
   329→  } catch (err) {
   330→    console.error("Failed to fetch pricing tiers:", err);
   331→    error.value = err instanceof Error ? err.message : "Failed to load pricing data";
   332→  } finally {
   333→    isLoading.value = false;
   334→  }
   335→};
   336→
   337→// Animation handling for pricing cards
   338→const hasBeenVisible = ref<boolean[]>(Array(4).fill(false)); // 3 main tiers + 1 enterprise
   339→
   340→// Set initial visibility after a short delay for a nice entrance effect
   341→setTimeout(() => {
   342→  hasBeenVisible.value = Array(4).fill(true);
   343→}, 100);
   344→
   345→// Helper functions to organize tiers for display
   346→const getMainTiers = (): TransformedTier[] => {
   347→  if (!pricingData.value.tiers) return [];
   348→  
   349→  // Filter out enterprise tier and get the 3 main tiers
   350→  const nonEnterpriseTiers = pricingData.value.tiers.filter(tier => 
   351→    tier.billingKey !== 'enterprise'
   352→  );
   353→  
   354→  // Sort by price (free first, then ascending by price)
   355→  return nonEnterpriseTiers.sort((a, b) => {
   356→    // Free tier always comes first
   357→    if (a.billingKey === 'free') return -1;
   358→    if (b.billingKey === 'free') return 1;
   359→    
   360→    // Sort others by monthly price
   361→    return a.monthlyPrice - b.monthlyPrice;
   362→  }).slice(0, 3); // Take only the first 3 tiers
   363→};
   364→
   365→// Fetch data on mount
   366→onMounted(() => {
   367→  fetchPricingTiers();
   368→});
