/**
 * Utility Classes Layer
 *
 * This file contains utility classes for the design system.
 * Uses @layer utilities for proper cascade ordering.
 *
 * Includes:
 * - Glass morphism effects (.glass-subtle, .glass-medium, .glass-strong, etc.)
 * - Focus ring utilities (.focus-ring, .focus-ring-error, etc.)
 * - Accessibility utilities (.skip-link, .focus-visible-only)
 * - Container query utilities
 * - Performance optimizations
 */

@layer utilities {
  /* ==========================================
   * SEMANTIC SURFACE COLOR UTILITIES
   * ========================================== */

  .bg-surface-primary {
    background-color: var(--color-surface-primary);
  }

  .bg-surface-secondary {
    background-color: var(--color-surface-secondary);
  }

  .bg-surface-tertiary {
    background-color: var(--color-surface-tertiary);
  }

  .bg-surface-inverse {
    background-color: var(--color-surface-inverse);
  }

  .text-inverse {
    color: var(--color-text-inverse);
  }

  /* ==========================================
   * GLASS MORPHISM EFFECTS
   * ========================================== */

  .glass-subtle {
    background: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(8px);
    -webkit-backdrop-filter: blur(8px);
    border: 1px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
  }

  [data-theme="dark"] .glass-subtle {
    background: rgba(31, 41, 55, 0.8);
    border: 1px solid rgba(55, 65, 81, 0.3);
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.2);
  }

  @media (prefers-reduced-motion: reduce) {
    .glass-subtle {
      backdrop-filter: none;
      -webkit-backdrop-filter: none;
    }
  }

  @media (prefers-reduced-transparency: reduce) {
    .glass-subtle {
      background: rgba(255, 255, 255, 0.95);
      backdrop-filter: none;
      -webkit-backdrop-filter: none;
    }

    [data-theme="dark"] .glass-subtle {
      background: rgba(17, 24, 39, 0.95);
    }
  }

  @media (prefers-reduced-data: reduce) {
    .glass-subtle {
      backdrop-filter: none;
      -webkit-backdrop-filter: none;
      background: rgba(255, 255, 255, 0.98);
    }

    [data-theme="dark"] .glass-subtle {
      background: rgba(17, 24, 39, 0.98);
    }
  }

  .glass-medium {
    background: rgba(255, 255, 255, 0.9);
    backdrop-filter: blur(12px);
    -webkit-backdrop-filter: blur(12px);
    border: 1px solid rgba(255, 255, 255, 0.3);
    box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
  }

  [data-theme="dark"] .glass-medium {
