      inset 0 1px 0 rgba(255, 255, 255, 0.6);
  }

  [data-theme="dark"] .glass-vibrant {
    background: rgba(31, 41, 55, 0.70);
    border: 1px solid rgba(55, 65, 81, 0.4);
    box-shadow:
      0 8px 32px rgba(0, 0, 0, 0.30),
      inset 0 1px 0 rgba(255, 255, 255, 0.1);
  }

  .glass-frosted {
    background: rgba(255, 255, 255, 0.50);
    backdrop-filter: blur(32px) saturate(200%);
    -webkit-backdrop-filter: blur(32px) saturate(200%);
    border: 1px solid rgba(255, 255, 255, 0.25);
    box-shadow:
      0 12px 48px rgba(0, 0, 0, 0.10),
      inset 0 1px 0 rgba(255, 255, 255, 0.5);
  }

  [data-theme="dark"] .glass-frosted {
    background: rgba(17, 24, 39, 0.50);
    border: 1px solid rgba(55, 65, 81, 0.35);
    box-shadow:
      0 12px 48px rgba(0, 0, 0, 0.40),
      inset 0 1px 0 rgba(255, 255, 255, 0.08);
  }

  /* Elevation Semantic Utilities */
  .elevation-surface {
    box-shadow: none;
    position: relative;
    z-index: 0;
  }

  .elevation-raised {
    box-shadow:
      0px 2px 4px rgba(0, 0, 0, 0.08),
      0px 0px 2px rgba(0, 0, 0, 0.06);
    position: relative;
    z-index: 1;
  }

  .elevation-overlay {
    box-shadow:
      0px 4px 8px rgba(0, 0, 0, 0.10),
      0px 0px 2px rgba(0, 0, 0, 0.06);
    position: relative;
    z-index: 10;
  }

  .elevation-floating {
    box-shadow:
      0px 8px 16px rgba(0, 0, 0, 0.14),
      0px 2px 4px rgba(0, 0, 0, 0.08);
    position: relative;
    z-index: 50;
  }

  .elevation-modal {
    box-shadow:
      0px 16px 32px rgba(0, 0, 0, 0.18),
      0px 4px 8px rgba(0, 0, 0, 0.10);
    position: relative;
    z-index: var(--z-index-modal);
  }

  /* Depth Layering - Overlapping elements */
  .layer-back {
    position: relative;
    z-index: 1;
    transform: translateY(8px);
    opacity: 0.90;
    filter: blur(0.5px);
  }

  .layer-middle {
    position: relative;
    z-index: 2;
    transform: translateY(4px);
    opacity: 0.95;
  }

  .layer-front {
    position: relative;
    z-index: 3;
    transform: translateY(0px);
  }

  /* Depth through scale and contrast */
  .depth-recede {
    opacity: 0.80;
    filter: brightness(0.95) saturate(0.90);
    transform: scale(0.98);
  }

  .depth-neutral {
    opacity: 1;
    filter: none;
    transform: scale(1);
  }

  .depth-advance {
    opacity: 1;
    filter: brightness(1.05) saturate(1.10);
    transform: scale(1.02);
  }

  /* Depth transitions */
  .depth-transition {
    transition:
      transform 300ms cubic-bezier(0, 0, 0.2, 1),
      box-shadow 300ms cubic-bezier(0, 0, 0.2, 1),
      opacity 300ms cubic-bezier(0, 0, 0.2, 1),
      filter 300ms cubic-bezier(0, 0, 0.2, 1);
  }

  .depth-transition:hover {
    transform: translateY(-4px) scale(1.02);
  }

  /* Reduce depth effects for accessibility */
  @media (prefers-reduced-motion: reduce) {
    .depth-transition {
      transition: none;
    }

    .depth-transition:hover {
      transform: none;
    }

    .layer-back,
    .layer-middle,
    .layer-front {
      transform: none;
      filter: none;
    }
  }

  /* Performance optimization for mobile */
  @media (max-width: 768px) {
    .glass-vibrant,
    .glass-frosted {
      backdrop-filter: blur(12px);
      -webkit-backdrop-filter: blur(12px);
    }

    .neomorph-flat,
    .neomorph-pressed,
    .neomorph-raised {
      box-shadow:
        4px 4px 8px rgba(0, 0, 0, 0.10),
        -4px -4px 8px rgba(255, 255, 255, 0.50);
    }
  }
}

