   986
   987  /* ==========================================
   988   * MICRO-INTERACTIONS & FEEDBACK STATES (2024-2025)
   989   * Research-based patterns for loading, success, error, and animations
   990   * ========================================== */
   991
   992  /* Skeleton Screens - Loading placeholders */
   993  .skeleton {
   994    background: linear-gradient(
   995      90deg,
   996      var(--color-gray-200) 0%,
   997      var(--color-gray-100) 50%,
   998      var(--color-gray-200) 100%
   999    );
  1000    background-size: 200% 100%;
  1001    animation: skeleton-shimmer 1.5s ease-in-out infinite;
  1002    border-radius: var(--radius-base);
  1003  }
  1004
  1005  @keyframes skeleton-shimmer {
  1006    0% {
  1007      background-position: 200% 0;
  1008    }
  1009    100% {
  1010      background-position: -200% 0;
  1011    }
  1012  }
  1013
  1014  [data-theme="dark"] .skeleton {
  1015    background: linear-gradient(
  1016      90deg,
  1017      var(--color-gray-700) 0%,
  1018      var(--color-gray-600) 50%,
  1019      var(--color-gray-700) 100%
  1020    );
  1021  }
  1022
  1023  .skeleton-text {
  1024    height: 1rem;
  1025    margin-bottom: 0.5rem;
  1026  }
  1027
  1028  .skeleton-title {
  1029    height: 1.5rem;
  1030    width: 60%;
  1031    margin-bottom: 0.75rem;
  1032  }
  1033
  1034  .skeleton-avatar {
  1035    width: 3rem;
  1036    height: 3rem;
  1037    border-radius: var(--radius-full);
  1038  }
  1039
  1040  /* Spinners - Loading indicators */
  1041  .spinner {
  1042    width: 2rem;
  1043    height: 2rem;
  1044    border: 3px solid var(--color-gray-200);
  1045    border-top-color: var(--color-primary-500);
  1046    border-radius: var(--radius-full);
  1047    animation: spinner-spin 0.8s linear infinite;
  1048  }
  1049
  1050  @keyframes spinner-spin {
  1051    to {
  1052      transform: rotate(360deg);
  1053    }
  1054  }
  1055
  1056  .spinner-sm {
  1057    width: 1rem;
  1058    height: 1rem;
  1059    border-width: 2px;
  1060  }
  1061
  1062  .spinner-lg {
  1063    width: 3rem;
  1064    height: 3rem;
  1065    border-width: 4px;
  1066  }
  1067
  1068  [data-theme="dark"] .spinner {
  1069    border-color: var(--color-gray-700);
  1070    border-top-color: var(--color-primary-400);
  1071  }
  1072
  1073  /* Dots Spinner */
  1074  .spinner-dots {
  1075    display: inline-flex;
  1076    gap: 0.5rem;
  1077  }
  1078
  1079  .spinner-dot {
  1080    width: 0.5rem;
  1081    height: 0.5rem;
  1082    background-color: var(--color-primary-500);
  1083    border-radius: var(--radius-full);
  1084    animation: spinner-dot-bounce 1.4s infinite ease-in-out both;
  1085  }
  1086
  1087  .spinner-dot:nth-child(1) {
  1088    animation-delay: -0.32s;
  1089  }
  1090
  1091  .spinner-dot:nth-child(2) {
  1092    animation-delay: -0.16s;
  1093  }
  1094
  1095  @keyframes spinner-dot-bounce {
  1096    0%, 80%, 100% {
  1097      transform: scale(0.8);
  1098      opacity: 0.5;
  1099    }
  1100    40% {
  1101      transform: scale(1.2);
  1102      opacity: 1;
  1103    }
  1104  }
  1105
  1106  /* Progress Bar */
  1107  .progress-bar {
  1108    width: 100%;
  1109    height: 0.5rem;
  1110    background-color: var(--color-gray-200);
  1111    border-radius: var(--radius-full);
  1112    overflow: hidden;
  1113  }
  1114
  1115  .progress-bar-fill {
  1116    height: 100%;
  1117    background-color: var(--color-primary-500);
  1118    border-radius: var(--radius-full);
  1119    transition: width 300ms ease-out;
  1120  }
  1121
  1122  .progress-bar-indeterminate .progress-bar-fill {
  1123    width: 30%;
  1124    animation: progress-indeterminate 1.5s ease-in-out infinite;
  1125  }
  1126
  1127  @keyframes progress-indeterminate {
  1128    0% {
  1129      transform: translateX(-100%);
  1130    }
  1131    100% {
  1132      transform: translateX(400%);
  1133    }
  1134  }
  1135
  1136  [data-theme="dark"] .progress-bar {
  1137    background-color: var(--color-gray-700);
  1138  }
  1139
  1140  /* Button Loading State */
  1141  .btn-loading {
  1142    position: relative;
  1143    color: transparent !important;
  1144    pointer-events: none;
  1145  }
  1146
  1147  .btn-loading::after {
  1148    content: '';
  1149    position: absolute;
  1150    width: 1rem;
  1151    height: 1rem;
  1152    top: 50%;
  1153    left: 50%;
  1154    margin-left: -0.5rem;
  1155    margin-top: -0.5rem;
  1156    border: 2px solid currentColor;
  1157    border-top-color: transparent;
  1158    border-radius: var(--radius-full);
  1159    animation: spinner-spin 0.6s linear infinite;
  1160  }
  1161
  1162  /* Toast Notifications */
  1163  .toast {
  1164    position: fixed;
  1165    z-index: var(--z-index-toast);
  1166    padding: var(--spacing-4) var(--spacing-6);
  1167    background-color: var(--color-surface-primary);
  1168    border: 1px solid var(--color-border-primary);
  1169    border-radius: var(--radius-xl);
  1170    box-shadow: var(--shadow-xl);
  1171    display: flex;
  1172    align-items: center;
  1173    gap: var(--spacing-3);
  1174    max-width: 24rem;
  1175    animation: toast-slide-in 300ms ease-out;
  1176  }
  1177
  1178  @keyframes toast-slide-in {
  1179    from {
  1180      transform: translateY(-100%);
  1181      opacity: 0;
  1182    }
  1183    to {
  1184      transform: translateY(0);
  1185      opacity: 1;
  1186    }
  1187  }
  1188
  1189  .toast-top-right {
  1190    top: var(--spacing-4);
  1191    right: var(--spacing-4);
  1192  }
  1193
  1194  .toast-top-center {
  1195    top: var(--spacing-4);
  1196    left: 50%;
  1197    transform: translateX(-50%);
  1198  }
  1199
  1200  .toast-bottom-right {
  1201    bottom: var(--spacing-4);
  1202    right: var(--spacing-4);
  1203  }
  1204
  1205  .toast-success {
  1206    border-left: 4px solid var(--color-success-500);
  1207  }
  1208
  1209  .toast-error {
  1210    border-left: 4px solid var(--color-error-500);
  1211  }
  1212
  1213  .toast-warning {
  1214    border-left: 4px solid var(--color-warning-500);
  1215  }
  1216
  1217  .toast-info {
  1218    border-left: 4px solid var(--color-info-500);
  1219  }
  1220
  1221  [data-theme="dark"] .toast {
  1222    background-color: var(--color-dark-surface-primary);
  1223    border-color: var(--color-dark-border-primary);
  1224  }
  1225
  1226  /* Success Checkmark Animation */
  1227  .success-checkmark {
  1228    width: 3rem;
  1229    height: 3rem;
  1230    border-radius: var(--radius-full);
  1231    display: flex;
  1232    align-items: center;
  1233    justify-content: center;
  1234    background-color: var(--color-success-500);
  1235    animation: success-scale 0.3s ease-out;
  1236  }
  1237
  1238  .success-checkmark::before {
  1239    content: '';
  1240    width: 0.5rem;
  1241    height: 1rem;
  1242    border: solid white;
  1243    border-width: 0 3px 3px 0;
  1244    transform: rotate(45deg);
  1245    animation: success-checkmark-draw 0.3s ease-out 0.1s both;
  1246  }
  1247
  1248  @keyframes success-scale {
  1249    0% {
  1250      transform: scale(0);
  1251    }
  1252    50% {
  1253      transform: scale(1.1);
  1254    }
  1255    100% {
  1256      transform: scale(1);
  1257    }
  1258  }
  1259
  1260  @keyframes success-checkmark-draw {
  1261    0% {
  1262      height: 0;
  1263    }
  1264    100% {
  1265      height: 1rem;
  1266    }
  1267  }
  1268
  1269  /* Error Shake Animation */
  1270  .error-shake {
  1271    animation: error-shake 0.5s ease-in-out;
  1272  }
  1273
  1274  @keyframes error-shake {
  1275    0%, 100% {
  1276      transform: translateX(0);
  1277    }
  1278    10%, 30%, 50%, 70%, 90% {
  1279      transform: translateX(-8px);
  1280    }
  1281    20%, 40%, 60%, 80% {
  1282      transform: translateX(8px);
  1283    }
  1284  }
  1285
  1286  /* Ripple Effect */
  1287  .ripple-container {
  1288    position: relative;
  1289    overflow: hidden;
  1290  }
  1291
  1292  .ripple {
  1293    position: absolute;
  1294    border-radius: var(--radius-full);
  1295    background-color: rgba(255, 255, 255, 0.6);
  1296    transform: scale(0);
  1297    animation: ripple-effect 0.6s ease-out;
  1298    pointer-events: none;
  1299  }
  1300
  1301  @keyframes ripple-effect {
  1302    to {
  1303      transform: scale(4);
  1304      opacity: 0;
  1305    }
  1306  }
  1307
  1308  [data-theme="dark"] .ripple {
  1309    background-color: rgba(255, 255, 255, 0.2);
  1310  }
  1311
  1312  /* Pulse Animation for Badges */
  1313  .pulse-badge {
  1314    position: relative;
  1315  }
  1316
  1317  .pulse-badge::before {
  1318    content: '';
  1319    position: absolute;
  1320    inset: -4px;
  1321    border-radius: inherit;
  1322    background-color: currentColor;
  1323    opacity: 0.3;
  1324    animation: pulse-ring 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  1325  }
  1326
  1327  @keyframes pulse-ring {
  1328    0%, 100% {
  1329      opacity: 0.3;
  1330      transform: scale(1);
  1331    }
  1332    50% {
  1333      opacity: 0;
  1334      transform: scale(1.5);
  1335    }
  1336  }
  1337
  1338  /* Number Counter Animation */
  1339  .counter-animate {
  1340    animation: counter-pop 0.3s ease-out;
  1341  }
  1342
  1343  @keyframes counter-pop {
  1344    0% {
  1345      transform: scale(1);
  1346    }
  1347    50% {
  1348      transform: scale(1.2);
  1349    }
  1350    100% {
  1351      transform: scale(1);
  1352    }
  1353  }
  1354
  1355  /* Heart/Like Animation */
  1356  .heart-animate {
  1357    animation: heart-bounce 0.3s ease-out;
  1358  }
  1359
  1360  @keyframes heart-bounce {
  1361    0% {
  1362      transform: scale(1);
  1363    }
  1364    25% {
  1365      transform: scale(1.3);
  1366    }
  1367    50% {
  1368      transform: scale(0.9);
  1369    }
  1370    75% {
  1371      transform: scale(1.1);
  1372    }
  1373    100% {
  1374      transform: scale(1);
  1375    }
  1376  }
  1377
  1378  /* Card Hover Lift */
  1379  .card-lift {
  1380    transition: transform 200ms ease-out, box-shadow 200ms ease-out;
  1381  }
  1382
  1383  .card-lift:hover {
  1384    transform: translateY(-4px);
  1385    box-shadow: var(--shadow-lg);
  1386  }
  1387
  1388  /* Link Underline Animation */
  1389  .link-underline {
  1390    position: relative;
  1391    text-decoration: none;
  1392  }
  1393
  1394  .link-underline::after {
  1395    content: '';
  1396    position: absolute;
  1397    bottom: 0;
  1398    left: 0;
  1399    width: 100%;
  1400    height: 2px;
  1401    background-color: currentColor;
  1402    transform: scaleX(0);
  1403    transform-origin: right;
  1404    transition: transform 300ms ease-out;
  1405  }
  1406
  1407  .link-underline:hover::after {
  1408    transform: scaleX(1);
  1409    transform-origin: left;
  1410  }
  1411
  1412  /* Focus Ring - Keyboard Only */
  1413  .focus-ring-keyboard {
  1414    outline: 2px solid transparent;
  1415    outline-offset: 2px;
  1416  }
  1417
  1418  .focus-ring-keyboard:focus {
  1419    outline: none;
  1420  }
  1421
  1422  .focus-ring-keyboard:focus-visible {
  1423    outline: 2px solid var(--color-primary-500);
  1424    outline-offset: 2px;
  1425  }
  1426
  1427  /* Bounce Animation */
  1428  .bounce-on-hover {
  1429    transition: transform 200ms cubic-bezier(0.68, -0.55, 0.265, 1.55);
  1430  }
  1431
  1432  .bounce-on-hover:hover {
  1433    transform: scale(1.1);
  1434  }
  1435
  1436  /* Fade In Animations */
  1437  .fade-in {
  1438    animation: fade-in 300ms ease-out;
  1439  }
  1440
  1441  /* Removed duplicate fade-in and fade-in-up keyframes - now in design-system.css */
  1442
  1443  /* Stagger Animations */
  1444  .stagger-item {
  1445    opacity: 0;
  1446    animation: fade-in-up 400ms ease-out forwards;
  1447  }
  1448
  1449  .stagger-item:nth-child(1) { animation-delay: 0ms; }
  1450  .stagger-item:nth-child(2) { animation-delay: 100ms; }
  1451  .stagger-item:nth-child(3) { animation-delay: 200ms; }
  1452  .stagger-item:nth-child(4) { animation-delay: 300ms; }
  1453  .stagger-item:nth-child(5) { animation-delay: 400ms; }
  1454  .stagger-item:nth-child(6) { animation-delay: 500ms; }
  1455
  1456  /* Accordion Expand/Collapse */
  1457  .accordion-content {
  1458    overflow: hidden;
  1459    transition: max-height 300ms ease-out, opacity 300ms ease-out;
  1460  }
  1461
  1462  .accordion-content[aria-hidden="true"] {
  1463    max-height: 0;
  1464    opacity: 0;
  1465  }
  1466
  1467  .accordion-content[aria-hidden="false"] {
  1468    max-height: 1000px;
  1469    opacity: 1;
  1470  }
  1471
  1472  /* Tooltip */
  1473  .tooltip {
  1474    position: absolute;
  1475    padding: var(--spacing-2) var(--spacing-3);
  1476    background-color: var(--color-gray-900);
  1477    color: white;
  1478    font-size: var(--font-size-sm);
  1479    border-radius: var(--radius-lg);
  1480    white-space: nowrap;
  1481    z-index: var(--z-index-tooltip);
  1482    opacity: 0;
  1483    pointer-events: none;
  1484    transition: opacity 200ms ease-out;
  1485  }
  1486
  1487  .tooltip-trigger:hover .tooltip,
  1488  .tooltip-trigger:focus .tooltip {
  1489    opacity: 1;
  1490  }
  1491
  1492  [data-theme="dark"] .tooltip {
  1493    background-color: var(--color-gray-100);
  1494    color: var(--color-gray-900);
  1495  }
  1496
  1497  /* Image Zoom on Hover */
  1498  .image-zoom-container {
  1499    overflow: hidden;
  1500    border-radius: inherit;
  1501  }
  1502
  1503  .image-zoom {
  1504    transition: transform 400ms ease-out;
  1505  }
  1506
  1507  .image-zoom-container:hover .image-zoom {
  1508    transform: scale(1.1);
  1509  }
  1510
  1511  /* Reduced Motion Support */
  1512  @media (prefers-reduced-motion: reduce) {
  1513    .skeleton,
  1514    .spinner,
  1515    .spinner-dot,
  1516    .progress-bar-fill,
  1517    .btn-loading::after,
  1518    .toast,
  1519    .success-checkmark,
  1520    .success-checkmark::before,
  1521    .error-shake,
  1522    .ripple,
  1523    .pulse-badge::before,
  1524    .counter-animate,
  1525    .heart-animate,
  1526    .card-lift,
  1527    .link-underline::after,
  1528    .bounce-on-hover,
  1529    .fade-in,
  1530    .fade-in-up,
  1531    .stagger-item,
  1532    .accordion-content,
  1533    .tooltip,
  1534    .image-zoom {
  1535      animation: none !important;
  1536      transition: none !important;
  1537    }
  1538
  1539    .card-lift:hover {
  1540      transform: none;
  1541    }
  1542
  1543    .bounce-on-hover:hover {
  1544      transform: none;
  1545    }
  1546
  1547    .image-zoom-container:hover .image-zoom {
  1548      transform: none;
  1549    }
  1550  }
  1551}
  1552

