@import "tailwindcss";
@plugin "@tailwindcss/typography";

/* Import style files into appropriate layers */
@import "./keyframes.css" layer(theme);
@import "./components.css" layer(components);
@import "./utilities.css" layer(utilities);

/* Dark mode variant */
@custom-variant dark (&:where([data-theme=dark], [data-theme=dark] *));

/* Accessibility-focused custom variants */
@custom-variant motion-safe {
  @media (prefers-reduced-motion: no-preference) {
    @slot;
  }
}

@custom-variant motion-reduce {
  @media (prefers-reduced-motion: reduce) {
    @slot;
  }
}

@custom-variant contrast-high {
  @media (prefers-contrast: high) {
    @slot;
  }
}

@custom-variant touch {
  @media (pointer: coarse) {
    @slot;
  }
}

@custom-variant mouse {
  @media (pointer: fine) {
    @slot;
  }
}

@custom-variant hover-supported {
  @media (hover: hover) {
    @slot;
  }
}

/* ============================================
   TAILWIND V4 DESIGN TOKENS
   Define ONCE here - generates Tailwind utilities
   ============================================ */

@theme {
  /* ===== BRAND COLORS ===== */

  /* Primary (Teal) - Full scale for flexibility */
  --color-primary-50: oklch(95% 0.03 200);
  --color-primary-100: oklch(90% 0.05 200);
  --color-primary-200: oklch(80% 0.08 200);
  --color-primary-300: oklch(70% 0.11 200);
  --color-primary-400: oklch(60% 0.14 200);
  --color-primary-500: oklch(50% 0.16 200);  /* Base: #007c89 */
  --color-primary-600: oklch(45% 0.14 200);
  --color-primary-700: oklch(35% 0.11 200);
  --color-primary-800: oklch(25% 0.08 200);
  --color-primary-900: oklch(15% 0.05 200);
  --color-primary-950: oklch(10% 0.03 200);

  /* ===== SEMANTIC COLORS ===== */

  --color-success: oklch(65% 0.17 160);  /* Green */
  --color-warning: oklch(75% 0.18 85);   /* Amber */
  --color-error: oklch(60% 0.22 25);     /* Red */
  --color-info: oklch(65% 0.18 250);     /* Blue */

  /* ===== NEUTRAL GRAYS ===== */

  --color-gray-50: oklch(98% 0 0);
  --color-gray-100: oklch(96% 0 0);
  --color-gray-200: oklch(90% 0 0);
  --color-gray-300: oklch(83% 0 0);
  --color-gray-400: oklch(64% 0 0);
  --color-gray-500: oklch(53% 0 0);
  --color-gray-600: oklch(45% 0 0);
  --color-gray-700: oklch(38% 0 0);
  --color-gray-800: oklch(27% 0 0);
  --color-gray-900: oklch(15% 0 0);
  --color-gray-950: oklch(10% 0 0);

  /* ===== TYPOGRAPHY ===== */

  --font-sans: 'Inter', ui-sans-serif, system-ui, -apple-system, sans-serif;
  --font-mono: 'JetBrains Mono', ui-monospace, 'SF Mono', monospace;

  /* Custom font size for hero sections */
  --font-size-display: clamp(3rem, 5vw + 1rem, 6rem);
  --font-size-display-2xl: clamp(3rem, 5vw, 4.5rem);  /* 48-72px hero */
  --font-size-display-xl: clamp(2.5rem, 4vw, 3.5rem); /* Section headings */
  --font-size-heading-card: clamp(1.875rem, 3vw, 2.25rem);

