   100  --font-size-heading-card: clamp(1.875rem, 3vw, 2.25rem);
   101
   102  /* ===== CUSTOM EFFECTS ===== */
   103
   104  /* Brand-specific shadows */
   105  --shadow-glow: 0 0 20px rgb(0 124 137 / 0.3);
   106  --shadow-glow-lg: 0 0 40px rgb(0 124 137 / 0.4);
   107
   108  /* Brutal/Neo-brutalism style */
   109  --shadow-brutal: 4px 4px 0 0 rgb(0 0 0 / 1);
   110
   111  /* ===== ANIMATION TOKENS ===== */
   112
   113  /* Custom easing curves */
   114  --ease-smooth: cubic-bezier(0.16, 1, 0.3, 1);
   115  --ease-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55);
   116  --ease-elastic: cubic-bezier(0.68, -0.55, 0.265, 1.55);
   117
   118  /* Durations - synced with constants/animations.ts */
   119  --duration-instant: 100ms;
   120  --duration-fast: 200ms;
   121  --duration-normal: 300ms;
   122  --duration-slow: 500ms;
   123  --duration-slower: 800ms;
   124  --duration-celebration: 2000ms;
   125
   126  /* ===== Z-INDEX SCALE ===== */
   127
   128  --z-dropdown: 1000;
   129  --z-sticky: 1020;
   130  --z-modal: 1050;
   131  --z-tooltip: 1070;
   132  --z-toast: 1080;
   133}
   134
   135/* ===== DARK MODE OVERRIDES ===== */
   136
   137@theme dark {
   138  /* Adjust primary color slightly lighter in dark mode for better visibility */
   139  --color-primary-500: oklch(55% 0.18 200);
   140}
   141
   142/* ============================================
