     1/**
     2 * Component Layer Styles (Converted from Tailwind Plugins)
     3 *
     4 * This file contains component base styles that were previously defined
     5 * as JavaScript plugins. Converted to pure CSS for Tailwind v4 best practices.
     6 *
     7 * Includes:
     8 * - Form controls (.form-control-base)
     9 * - Buttons (.btn-base, .btn-sm, .btn-md, .btn-lg, .btn-xl)
    10 * - Cards (.card-base, .card-interactive)
    11 * - Badges (.badge-base)
    12 * - Modals (.modal-backdrop, .modal-panel)
    13 * - Input groups (.input-group)
    14 */
    15
    16@layer components {
    17  /* ==========================================
    18   * FORM CONTROL BASE
    19   * ========================================== */
    20  .form-control-base {
    21    width: 100%;
    22    padding: var(--spacing-2) var(--spacing-4);
    23    border: 1px solid var(--color-border-primary, var(--color-neutral-300));
    24    border-radius: var(--radius-lg);
    25    background-color: var(--color-surface-primary, #ffffff);
    26    color: var(--color-neutral-900);
    27    font-size: var(--font-size-base);
    28    line-height: var(--line-height-normal);
    29    transition: border-color 150ms ease, box-shadow 150ms ease;
    30  }
    31
    32  .form-control-base::placeholder {
    33    color: var(--color-neutral-400);
    34  }
    35
    36  .form-control-base:focus {
    37    outline: none;
    38    border-color: var(--color-border-focus, var(--color-primary-500));
    39    box-shadow: var(--shadow-focus-primary);
    40  }
    41
    42  .form-control-base:disabled {
    43    opacity: 0.6;
    44    cursor: not-allowed;
    45    background-color: var(--color-neutral-100);
    46    color: var(--color-neutral-500);
    47  }
    48
    49  .form-control-base[aria-invalid="true"] {
    50    border-color: var(--color-border-error, var(--color-error-500));
    51  }
    52
    53  .form-control-base[aria-invalid="true"]:focus {
    54    box-shadow: var(--shadow-focus-error);
    55  }
    56
    57  /* Dark mode support */
    58  [data-theme="dark"] .form-control-base {
    59    background-color: var(--color-dark-surface-primary);
    60    border-color: var(--color-dark-border-primary);
    61    color: var(--color-dark-text-primary);
    62  }
    63
    64  [data-theme="dark"] .form-control-base::placeholder {
    65    color: var(--color-neutral-500);
    66  }
    67
    68  [data-theme="dark"] .form-control-base:disabled {
    69    background-color: var(--color-dark-surface-secondary);
    70    color: var(--color-neutral-600);
    71  }
    72
    73  /* ==========================================
    74   * BUTTON BASE
    75   * ========================================== */
