   991
   992  /* Skeleton Screens - Loading placeholders */
   993  .skeleton {
   994    background: linear-gradient(
   995      90deg,
   996      var(--color-gray-200) 0%,
   997      var(--color-gray-100) 50%,
   998      var(--color-gray-200) 100%
   999    );
  1000    background-size: 200% 100%;
  1001    animation: skeleton-shimmer 1.5s ease-in-out infinite;
  1002    border-radius: var(--radius-base);
  1003  }
  1004
  1005  @keyframes skeleton-shimmer {
