  1007      background-position: 200% 0;
  1008    }
  1009    100% {
  1010      background-position: -200% 0;
  1011    }
  1012  }
  1013
  1014  [data-theme="dark"] .skeleton {
  1015    background: linear-gradient(
  1016      90deg,
  1017      var(--color-gray-700) 0%,
  1018      var(--color-gray-600) 50%,
  1019      var(--color-gray-700) 100%
  1020    );
  1021  }
  1022
  1023  .skeleton-text {
