  1077  }
  1078
  1079  .spinner-dot {
  1080    width: 0.5rem;
  1081    height: 0.5rem;
  1082    background-color: var(--color-primary-500);
  1083    border-radius: var(--radius-full);
  1084    animation: spinner-dot-bounce 1.4s infinite ease-in-out both;
  1085  }
  1086
  1087  .spinner-dot:nth-child(1) {
  1088    animation-delay: -0.32s;
  1089  }
  1090
  1091  .spinner-dot:nth-child(2) {
  1092    animation-delay: -0.16s;
  1093  }
  1094
  1095  @keyframes spinner-dot-bounce {
  1096    0%, 80%, 100% {
  1097      transform: scale(0.8);
  1098      opacity: 0.5;
  1099    }
  1100    40% {
  1101      transform: scale(1.2);
  1102      opacity: 1;
  1103    }
  1104  }
  1105
  1106  /* Progress Bar */
  1107  .progress-bar {
  1108    width: 100%;
  1109    height: 0.5rem;
  1110    background-color: var(--color-gray-200);
  1111    border-radius: var(--radius-full);
