  1113  }
  1114
  1115  .progress-bar-fill {
  1116    height: 100%;
  1117    background-color: var(--color-primary-500);
  1118    border-radius: var(--radius-full);
  1119    transition: width 300ms ease-out;
  1120  }
  1121
  1122  .progress-bar-indeterminate .progress-bar-fill {
  1123    width: 30%;
  1124    animation: progress-indeterminate 1.5s ease-in-out infinite;
  1125  }
  1126
  1127  @keyframes progress-indeterminate {
  1128    0% {
  1129      transform: translateX(-100%);
  1130    }
  1131    100% {
  1132      transform: translateX(400%);
  1133    }
  1134  }
  1135
  1136  [data-theme="dark"] .progress-bar {
  1137    background-color: var(--color-gray-700);
  1138  }
  1139
  1140  /* Button Loading State */
  1141  .btn-loading {
  1142    position: relative;
  1143    color: transparent !important;
  1144    pointer-events: none;
  1145  }
  1146
  1147  .btn-loading::after {
  1148    content: '';
  1149    position: absolute;
  1150    width: 1rem;
  1151    height: 1rem;
  1152    top: 50%;
  1153    left: 50%;
  1154    margin-left: -0.5rem;
  1155    margin-top: -0.5rem;
  1156    border: 2px solid currentColor;
