    17}
    18
    19@custom-variant motion-reduce {
    20  @media (prefers-reduced-motion: reduce) {
    21    @slot;
    22  }
    23}
    24
    25@custom-variant contrast-high {
    26  @media (prefers-contrast: high) {
    27    @slot;
    28  }
    29}
    30
    31@custom-variant touch {
    32  @media (pointer: coarse) {
    33    @slot;
