    49/* ============================================
    50   TAILWIND V4 DESIGN TOKENS
    51   Define ONCE here - generates Tailwind utilities
    52   ============================================ */
    53
    54@theme {
    55  /* ===== BRAND COLORS ===== */
    56
    57  /* Primary (Teal) - Full scale for flexibility */
    58  --color-primary-50: oklch(95% 0.03 200);
    59  --color-primary-100: oklch(90% 0.05 200);
    60  --color-primary-200: oklch(80% 0.08 200);
    61  --color-primary-300: oklch(70% 0.11 200);
    62  --color-primary-400: oklch(60% 0.14 200);
    63  --color-primary-500: oklch(50% 0.16 200);  /* Base: #007c89 */
    64  --color-primary-600: oklch(45% 0.14 200);
    65  --color-primary-700: oklch(35% 0.11 200);
    66  --color-primary-800: oklch(25% 0.08 200);
    67  --color-primary-900: oklch(15% 0.05 200);
    68  --color-primary-950: oklch(10% 0.03 200);
    69
    70  /* ===== SEMANTIC COLORS ===== */
    71
    72  --color-success: oklch(65% 0.17 160);  /* Green */
    73  --color-warning: oklch(75% 0.18 85);   /* Amber */
    74  --color-error: oklch(60% 0.22 25);     /* Red */
    75  --color-info: oklch(65% 0.18 250);     /* Blue */
    76
    77  /* ===== NEUTRAL GRAYS ===== */
    78
    79  --color-gray-50: oklch(98% 0 0);
    80  --color-gray-100: oklch(96% 0 0);
    81  --color-gray-200: oklch(90% 0 0);
    82  --color-gray-300: oklch(83% 0 0);
    83  --color-gray-400: oklch(64% 0 0);
    84  --color-gray-500: oklch(53% 0 0);
    85  --color-gray-600: oklch(45% 0 0);
    86  --color-gray-700: oklch(38% 0 0);
    87  --color-gray-800: oklch(27% 0 0);
    88  --color-gray-900: oklch(15% 0 0);
    89  --color-gray-950: oklch(10% 0 0);
    90
    91  /* ===== TYPOGRAPHY ===== */
    92
    93  --font-sans: 'Inter', ui-sans-serif, system-ui, -apple-system, sans-serif;
    94  --font-mono: 'JetBrains Mono', ui-monospace, 'SF Mono', monospace;
    95
    96  /* Custom font size for hero sections */
    97  --font-size-display: clamp(3rem, 5vw + 1rem, 6rem);
    98  --font-size-display-2xl: clamp(3rem, 5vw, 4.5rem);  /* 48-72px hero */
    99  --font-size-display-xl: clamp(2.5rem, 4vw, 3.5rem); /* Section headings */
   100  --font-size-heading-card: clamp(1.875rem, 3vw, 2.25rem);
   101
   102  /* ===== CUSTOM EFFECTS ===== */
   103
   104  /* Brand-specific shadows */
   105  --shadow-glow: 0 0 20px rgb(0 124 137 / 0.3);
   106  --shadow-glow-lg: 0 0 40px rgb(0 124 137 / 0.4);
   107
   108  /* Brutal/Neo-brutalism style */
   109  --shadow-brutal: 4px 4px 0 0 rgb(0 0 0 / 1);
   110
   111  /* ===== ANIMATION TOKENS ===== */
   112
   113  /* Custom easing curves */
   114  --ease-smooth: cubic-bezier(0.16, 1, 0.3, 1);
   115  --ease-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55);
   116  --ease-elastic: cubic-bezier(0.68, -0.55, 0.265, 1.55);
   117
   118  /* Durations - synced with constants/animations.ts */
   119  --duration-instant: 100ms;
   120  --duration-fast: 200ms;
   121  --duration-normal: 300ms;
   122  --duration-slow: 500ms;
   123  --duration-slower: 800ms;
   124  --duration-celebration: 2000ms;
   125
   126  /* ===== Z-INDEX SCALE ===== */
   127
   128  --z-dropdown: 1000;
   129  --z-sticky: 1020;
   130  --z-modal: 1050;
   131  --z-tooltip: 1070;
   132  --z-toast: 1080;
   133}
   134
   135/* ===== DARK MODE OVERRIDES ===== */
   136
   137@theme dark {
   138  /* Adjust primary color slightly lighter in dark mode for better visibility */
   139  --color-primary-500: oklch(55% 0.18 200);
   140}
   141
   142/* ============================================
   143   UTILITY CLASSES
   144   ============================================ */
   145
