    93→
    94→  @media (prefers-reduced-data: reduce) {
    95→    backdrop-filter: none;
    96→    -webkit-backdrop-filter: none;
    97→    background: rgba(255, 255, 255, 0.98);
    98→
    99→    [data-theme="dark"] & {
   100→      background: rgba(17, 24, 39, 0.98);
   101→    }
   102→  }
   103→}
   104→
   105→@utility glass-card {
   106→  background: rgba(255, 255, 255, 0.85);
   107→  backdrop-filter: blur(10px);
   108→  -webkit-backdrop-filter: blur(10px);
   109→  border: 1px solid rgba(255, 255, 255, 0.25);
   110→  border-radius: var(--radius-xl);
   111→  box-shadow: var(--shadow-md);
   112→  padding: var(--spacing-6);
   113→
   114→  [data-theme="dark"] & {
   115→    background: rgba(31, 41, 55, 0.85);
   116→    border: 1px solid rgba(55, 65, 81, 0.35);
   117→  }
   118→
   119→  @media (prefers-reduced-motion: reduce) {
   120→    backdrop-filter: none;
