   151→  }
   152→
   153→  @media (prefers-reduced-data: reduce) {
   154→    backdrop-filter: none;
   155→    -webkit-backdrop-filter: none;
   156→    background: rgba(255, 255, 255, 0.98);
   157→
   158→    [data-theme="dark"] & {
   159→      background: rgba(17, 24, 39, 0.98);
   160→    }
   161→  }
   162→}
   163→
   164→@utility glass-modal {
   165→  background: rgba(255, 255, 255, 0.92);
   166→  backdrop-filter: blur(16px);
   167→  -webkit-backdrop-filter: blur(16px);
   168→  border: 1px solid rgba(255, 255, 255, 0.35);
   169→  border-radius: var(--radius-2xl);
   170→  box-shadow: var(--shadow-2xl);
   171→
   172→  [data-theme="dark"] & {
   173→    background: rgba(17, 24, 39, 0.92);
   174→    border: 1px solid rgba(55, 65, 81, 0.45);
   175→  }
   176→
   177→  @media (prefers-reduced-motion: reduce) {
   178→    backdrop-filter: none;
   179→    -webkit-backdrop-filter: none;
   180→  }
   181→
   182→  @media (prefers-reduced-data: reduce) {
   183→    backdrop-filter: none;
   184→    -webkit-backdrop-filter: none;
   185→    background: rgba(255, 255, 255, 0.98);
   186→
   187→    [data-theme="dark"] & {
   188→      background: rgba(17, 24, 39, 0.98);
   189→    }
   190→  }
   191→}
   192→
   193→/* ==========================================
   194→ * FOCUS RING UTILITIES
   195→ * ========================================== */
   196→
   197→@utility focus-ring {
   198→  &:focus,
   199→  &:focus-visible {
   200→    outline: none;
   201→    box-shadow: var(--shadow-focus-primary);
