     1→/**
     2→ * Component Layer Styles (Converted from Tailwind Plugins)
     3→ *
     4→ * This file contains component base styles that were previously defined
     5→ * as JavaScript plugins. Converted to pure CSS for Tailwind v4 best practices.
     6→ *
     7→ * Includes:
     8→ * - Form controls (.form-control-base)
     9→ * - Buttons (.btn-base, .btn-sm, .btn-md, .btn-lg, .btn-xl)
    10→ * - Cards (.card-base, .card-interactive)
    11→ * - Badges (.badge-base)
    12→ * - Modals (.modal-backdrop, .modal-panel)
    13→ * - Input groups (.input-group)
    14→ */
    15→
    16→@layer components {
    17→  /* ==========================================
    18→   * FORM CONTROL BASE
    19→   * ========================================== */
    20→  .form-control-base {
    21→    width: 100%;
    22→    padding: var(--spacing-2) var(--spacing-4);
    23→    border: 1px solid var(--color-border-primary, var(--color-neutral-300));
    24→    border-radius: var(--radius-lg);
    25→    background-color: var(--color-surface-primary, #ffffff);
    26→    color: var(--color-neutral-900);
    27→    font-size: var(--font-size-base);
    28→    line-height: var(--line-height-normal);
    29→    transition: border-color 150ms ease, box-shadow 150ms ease;
    30→  }
    31→
    32→  .form-control-base::placeholder {
    33→    color: var(--color-neutral-400);
    34→  }
    35→
    36→  .form-control-base:focus {
    37→    outline: none;
    38→    border-color: var(--color-border-focus, var(--color-primary-500));
    39→    box-shadow: var(--shadow-focus-primary);
    40→  }
    41→
    42→  .form-control-base:disabled {
    43→    opacity: 0.6;
    44→    cursor: not-allowed;
    45→    background-color: var(--color-neutral-100);
    46→    color: var(--color-neutral-500);
    47→  }
    48→
    49→  .form-control-base[aria-invalid="true"] {
    50→    border-color: var(--color-border-error, var(--color-error-500));
    51→  }
    52→
    53→  .form-control-base[aria-invalid="true"]:focus {
    54→    box-shadow: var(--shadow-focus-error);
    55→  }
    56→
    57→  /* Dark mode support */
    58→  [data-theme="dark"] .form-control-base {
    59→    background-color: var(--color-dark-surface-primary);
    60→    border-color: var(--color-dark-border-primary);
    61→    color: var(--color-dark-text-primary);
    62→  }
    63→
    64→  [data-theme="dark"] .form-control-base::placeholder {
    65→    color: var(--color-neutral-500);
    66→  }
    67→
    68→  [data-theme="dark"] .form-control-base:disabled {
    69→    background-color: var(--color-dark-surface-secondary);
    70→    color: var(--color-neutral-600);
    71→  }
    72→
    73→  /* ==========================================
    74→   * BUTTON BASE
    75→   * ========================================== */
    76→  .btn-base {
    77→    display: inline-flex;
    78→    align-items: center;
    79→    justify-content: center;
    80→    gap: var(--spacing-2);
    81→    font-weight: var(--font-weight-medium);
    82→    border-radius: var(--radius-lg);
    83→    transition: all 150ms ease;
    84→    cursor: pointer;
    85→    text-decoration: none;
    86→    white-space: nowrap;
    87→    user-select: none;
    88→  }
    89→
    90→  .btn-base:disabled,
    91→  .btn-base[aria-disabled="true"] {
    92→    opacity: 0.6;
    93→    pointer-events: none;
    94→    cursor: not-allowed;
    95→  }
    96→
    97→  .btn-base:focus-visible {
    98→    outline: none;
    99→    box-shadow: var(--shadow-focus-primary);
   100→  }
   101→
   102→  /* Button size variants */
   103→  .btn-xs {
   104→    padding: var(--spacing-1) var(--spacing-2);
   105→    font-size: var(--font-size-xs);
   106→    line-height: var(--line-height-tight);
   107→  }
   108→
   109→  .btn-sm {
   110→    padding: var(--spacing-1_5) var(--spacing-3);
   111→    font-size: var(--font-size-sm);
   112→    line-height: var(--line-height-tight);
   113→  }
   114→
   115→  .btn-md {
   116→    padding: var(--spacing-2) var(--spacing-4);
   117→    font-size: var(--font-size-base);
   118→    line-height: var(--line-height-tight);
   119→  }
   120→
   121→  .btn-lg {
   122→    padding: var(--spacing-2_5) var(--spacing-6);
   123→    font-size: var(--font-size-lg);
   124→    line-height: var(--line-height-tight);
   125→  }
   126→
   127→  .btn-xl {
   128→    padding: var(--spacing-3) var(--spacing-8);
   129→    font-size: var(--font-size-xl);
   130→    line-height: var(--line-height-tight);
   131→  }
   132→
   133→  /* ==========================================
   134→   * CARD BASE
   135→   * ========================================== */
   136→  .card-base {
   137→    background-color: var(--color-surface-primary, #ffffff);
   138→    border: 1px solid var(--color-border-primary, var(--color-neutral-200));
   139→    border-radius: var(--radius-xl);
   140→    box-shadow: var(--shadow-sm);
   141→    transition: box-shadow 200ms ease, transform 200ms ease;
   142→  }
   143→
   144→  .card-base.card-interactive {
   145→    cursor: pointer;
   146→  }
   147→
   148→  .card-base.card-interactive:hover {
   149→    box-shadow: var(--shadow-md);
   150→    transform: translateY(-2px);
   151→  }
   152→
   153→  .card-base.card-interactive:active {
   154→    transform: translateY(0);
   155→    box-shadow: var(--shadow-sm);
   156→  }
   157→
   158→  /* Card size variants */
   159→  .card-sm {
   160→    padding: var(--spacing-4);
   161→  }
   162→
   163→  .card-md {
   164→    padding: var(--spacing-6);
   165→  }
   166→
   167→  .card-lg {
   168→    padding: var(--spacing-8);
   169→  }
   170→
   171→  /* Dark mode support */
   172→  [data-theme="dark"] .card-base {
   173→    background-color: var(--color-dark-surface-primary);
   174→    border-color: var(--color-dark-border-primary);
   175→  }
   176→
   177→  /* ==========================================
   178→   * BADGE BASE
   179→   * ========================================== */
   180→  .badge-base {
   181→    display: inline-flex;
   182→    align-items: center;
   183→    gap: var(--spacing-1);
   184→    padding: var(--spacing-1) var(--spacing-2);
   185→    font-size: var(--font-size-xs);
   186→    font-weight: var(--font-weight-medium);
   187→    text-transform: uppercase;
   188→    border-radius: var(--radius-base);
   189→    letter-spacing: 0.05em;
   190→    line-height: var(--line-height-tight);
   191→    white-space: nowrap;
   192→  }
   193→
   194→  /* Badge size variants */
   195→  .badge-sm {
   196→    padding: calc(var(--spacing-1) * 0.5) var(--spacing-1_5);
   197→    font-size: 0.625rem; /* 10px */
   198→  }
   199→
   200→  .badge-md {
   201→    padding: var(--spacing-1) var(--spacing-2);
   202→    font-size: var(--font-size-xs);
   203→  }
   204→
   205→  .badge-lg {
   206→    padding: var(--spacing-1_5) var(--spacing-3);
   207→    font-size: var(--font-size-sm);
   208→  }
   209→
   210→  /* ==========================================
   211→   * MODAL BASE
   212→   * ========================================== */
   213→  .modal-backdrop {
   214→    position: fixed;
   215→    inset: 0;
   216→    background-color: rgb(0 0 0 / 0.5);
   217→    backdrop-filter: blur(4px);
   218→    z-index: var(--z-modal-backdrop);
   219→    display: flex;
   220→    align-items: center;
   221→    justify-content: center;
   222→    padding: var(--spacing-4);
   223→  }
   224→
   225→  .modal-panel {
   226→    background-color: var(--color-surface-primary, #ffffff);
   227→    border-radius: var(--radius-2xl);
   228→    box-shadow: var(--shadow-2xl);
   229→    max-width: 90vw;
   230→    max-height: 90vh;
   231→    overflow: auto;
   232→    position: relative;
   233→  }
   234→
   235→  [data-theme="dark"] .modal-panel {
   236→    background-color: var(--color-dark-surface-primary);
   237→  }
   238→
   239→  /* ==========================================
   240→   * INPUT GROUP (Icon + Input pattern)
   241→   * ========================================== */
