   123→
   124→  /* ===== SPACING SYSTEM ===== */
   125→  /* Base spacing unit for calculations */
   126→  --space-unit: 0.25rem; /* 4px base */
   127→
   128→  /* Fixed spacing for precise control */
   129→  --spacing-0: 0;
   130→  --spacing-px: 1px;
   131→  --spacing-0_5: 0.125rem;  /* 2px */
   132→  --spacing-1: 0.25rem;     /* 4px */
   133→  --spacing-1_5: 0.375rem;  /* 6px */
   134→  --spacing-2: 0.5rem;      /* 8px */
   135→  --spacing-2_5: 0.625rem;  /* 10px */
   136→  --spacing-3: 0.75rem;     /* 12px */
   137→  --spacing-3_5: 0.875rem;  /* 14px */
   138→  --spacing-4: 1rem;        /* 16px */
   139→  --spacing-5: 1.25rem;     /* 20px */
   140→  --spacing-6: 1.5rem;      /* 24px */
   141→  --spacing-7: 1.75rem;     /* 28px */
   142→  --spacing-8: 2rem;        /* 32px */
   143→  --spacing-9: 2.25rem;     /* 36px */
   144→  --spacing-10: 2.5rem;     /* 40px */
   145→  --spacing-11: 2.75rem;    /* 44px */
   146→  --spacing-12: 3rem;       /* 48px */
   147→  --spacing-14: 3.5rem;     /* 56px */
   148→  --spacing-16: 4rem;       /* 64px */
   149→  --spacing-20: 5rem;       /* 80px */
   150→  --spacing-24: 6rem;       /* 96px */
   151→  --spacing-28: 7rem;       /* 112px */
   152→  --spacing-32: 8rem;       /* 128px */
   153→
   154→  /* Fluid spacing for responsive layouts - scales smoothly with viewport */
   155→  --space-xs: clamp(0.5rem, 0.4rem + 0.5vw, 0.75rem);     /* 8px → 12px */
   156→  --space-sm: clamp(0.75rem, 0.65rem + 0.5vw, 1rem);      /* 12px → 16px */
   157→  --space-md: clamp(1rem, 0.85rem + 0.75vw, 1.5rem);      /* 16px → 24px */
   158→  --space-lg: clamp(1.5rem, 1.2rem + 1.5vw, 2.5rem);      /* 24px → 40px */
   159→  --space-xl: clamp(2rem, 1.5rem + 2.5vw, 3.5rem);        /* 32px → 56px */
