   239→    radial-gradient(at 40% 80%, rgba(236, 72, 153, 0.06) 0px, transparent 50%);
   240→
   241→  /* ===== TYPOGRAPHY ===== */
   242→  --font-family-sans: 'Inter', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, 'Noto Sans', sans-serif;
   243→  --font-family-mono: 'JetBrains Mono', 'SF Mono', Monaco, Inconsolata, 'Roboto Mono', 'Source Code Pro', monospace;
   244→  --font-family-emoji: 'Noto Color Emoji', 'Apple Color Emoji', 'Segoe UI Emoji', sans-serif;
   245→
   246→  /* Font Weights */
   247→  --font-weight-thin: 100;
   248→  --font-weight-extralight: 200;
   249→  --font-weight-light: 300;
   250→  --font-weight-normal: 400;
   251→  --font-weight-medium: 500;
   252→  --font-weight-semibold: 600;
   253→  --font-weight-bold: 700;
   254→  --font-weight-extrabold: 800;
   255→  --font-weight-black: 900;
   256→
   257→  /* Font Sizes - Fluid typography with clamp() for smooth scaling */
   258→  --font-size-xs: clamp(0.75rem, 0.7rem + 0.25vw, 0.875rem);      /* 12px → 14px */
   259→  --font-size-sm: clamp(0.875rem, 0.8rem + 0.375vw, 1rem);        /* 14px → 16px */
