   262→  --font-size-xl: clamp(1.25rem, 1.1rem + 0.75vw, 1.5rem);        /* 20px → 24px */
   263→  --font-size-2xl: clamp(1.5rem, 1.3rem + 1vw, 2rem);             /* 24px → 32px */
   264→  --font-size-3xl: clamp(1.875rem, 1.6rem + 1.375vw, 2.5rem);     /* 30px → 40px */
   265→  --font-size-4xl: clamp(2.25rem, 1.9rem + 1.75vw, 3rem);         /* 36px → 48px */
