   305→  --z-index-toast: 1080;
   306→  --z-index-max: 2147483647;
   307→
   308→  /* ===== ANIMATION UTILITIES ===== */
   309→  --animation-pulse: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
   310→  --animation-bounce: bounce 1s infinite;
   311→  --animation-spin: spin 1s linear infinite;
   312→  --animation-ping: ping 1s cubic-bezier(0, 0, 0.2, 1) infinite;
   313→  --animation-fade-in: fadeIn var(--transition-base);
   314→  --animation-fade-out: fadeOut var(--transition-base);
   315→  --animation-slide-in-up: slideInUp var(--transition-medium) var(--ease-out);
