   377→/* ===== KEYFRAME ANIMATIONS ===== */
   378→@keyframes fadeIn {
   379→  from {
   380→    opacity: 0;
   381→  }
   382→  to {
   383→    opacity: 1;
   384→  }
   385→}
   386→
   387→@keyframes fadeOut {
   388→  from {
   389→    opacity: 1;
   390→  }
   391→  to {
   392→    opacity: 0;
   393→  }
   394→}
   395→
   396→@keyframes slideInUp {
   397→  from {
   398→    opacity: 0;
   399→    transform: translateY(1rem);
   400→  }
   401→  to {
   402→    opacity: 1;
   403→    transform: translateY(0);
   404→  }
   405→}
   406→
   407→@keyframes slideInDown {
   408→  from {
   409→    opacity: 0;
   410→    transform: translateY(-1rem);
   411→  }
   412→  to {
   413→    opacity: 1;
   414→    transform: translateY(0);
   415→  }
   416→}
   417→
   418→@keyframes scaleIn {
   419→  from {
   420→    opacity: 0;
   421→    transform: scale(0.9);
   422→  }
   423→  to {
   424→    opacity: 1;
   425→    transform: scale(1);
   426→  }
   427→}
   428→
   429→@keyframes scaleOut {
   430→  from {
   431→    opacity: 1;
   432→    transform: scale(1);
   433→  }
   434→  to {
   435→    opacity: 0;
   436→    transform: scale(0.9);
   437→  }
   438→}
   439→
   440→@keyframes pulse {
   441→  0%, 100% {
   442→    opacity: 1;
   443→  }
   444→  50% {
   445→    opacity: 0.5;
   446→  }
   447→}
   448→
   449→@keyframes bounce {
   450→  0%, 100% {
   451→    transform: translateY(-25%);
   452→    animation-timing-function: cubic-bezier(0.8, 0, 1, 1);
   453→  }
   454→  50% {
   455→    transform: none;
   456→    animation-timing-function: cubic-bezier(0, 0, 0.2, 1);
   457→  }
   458→}
   459→
   460→@keyframes spin {
   461→  to {
   462→    transform: rotate(360deg);
   463→  }
   464→}
   465→
   466→@keyframes ping {
   467→  75%, 100% {
   468→    transform: scale(2);
   469→    opacity: 0;
   470→  }
   471→}
