   463→  position: relative;
   464→}
   465→
   466→.bg-gradient-mesh-overlay::before {
   467→  content: '';
   468→  position: absolute;
   469→  inset: 0;
   470→  background: var(--gradient-mesh-overlay);
   471→  pointer-events: none;
   472→  z-index: 1;
   473→}
   474→
   475→/* Text Gradients */
   476→.text-gradient-primary {
   477→  background: var(--gradient-accent-primary);
   478→  -webkit-background-clip: text;
   479→  background-clip: text;
   480→  -webkit-text-fill-color: transparent;
   481→}
   482→
   483→.text-gradient-purple {
   484→  background: var(--gradient-accent-purple);
   485→  -webkit-background-clip: text;
   486→  background-clip: text;
   487→  -webkit-text-fill-color: transparent;
   488→}
   489→
