   357→/* ===== KEYFRAME ANIMATIONS ===== */
   358→@keyframes fadeIn {
   359→  from {
   360→    opacity: 0;
   361→  }
   362→  to {
   363→    opacity: 1;
   364→  }
   365→}
   366→
   367→@keyframes fadeOut {
   368→  from {
