   370→  }
   371→  to {
   372→    opacity: 0;
   373→  }
   374→}
   375→
   376→@keyframes slideInUp {
   377→  from {
   378→    opacity: 0;
   379→    transform: translateY(1rem);
   380→  }
   381→  to {
   382→    opacity: 1;
   383→    transform: translateY(0);
   384→  }
   385→}
   386→
   387→@keyframes slideInDown {
