   391→  }
   392→  to {
   393→    opacity: 1;
   394→    transform: translateY(0);
   395→  }
   396→}
   397→
   398→@keyframes scaleIn {
   399→  from {
   400→    opacity: 0;
   401→    transform: scale(0.9);
   402→  }
   403→  to {
   404→    opacity: 1;
   405→    transform: scale(1);
   406→  }
   407→}
   408→
   409→@keyframes scaleOut {
   410→  from {
   411→    opacity: 1;
   412→    transform: scale(1);
   413→  }
   414→  to {
   415→    opacity: 0;
   416→    transform: scale(0.9);
   417→  }
   418→}
   419→
   420→@keyframes pulse {
   421→  0%, 100% {
   422→    opacity: 1;
   423→  }
   424→  50% {
   425→    opacity: 0.5;
   426→  }
   427→}
   428→
   429→@keyframes bounce {
