   836→  font-weight: var(--font-weight-medium);
   837→}
   838→
   839→.connection-indicator--connected {
   840→  background: var(--color-success-100);
   841→  color: var(--color-success-700);
   842→  border: 1px solid var(--color-success-200);
   843→}
   844→
   845→.connection-indicator--connecting {
   846→  background: var(--color-warning-100);
   847→  color: var(--color-warning-700);
   848→  border: 1px solid var(--color-warning-200);
   849→}
   850→
   851→.connection-indicator--disconnected {
   852→  background: var(--color-error-100);
   853→  color: var(--color-error-700);
   854→  border: 1px solid var(--color-error-200);
   855→}
   856→
   857→[data-theme="dark"] .connection-indicator--connected {
   858→  background: var(--color-success-950);
   859→  color: var(--color-success-300);
   860→  border: 1px solid var(--color-success-800);
   861→}
   862→
   863→[data-theme="dark"] .connection-indicator--connecting {
   864→  background: var(--color-warning-950);
   865→  color: var(--color-warning-300);
   866→  border: 1px solid var(--color-warning-800);
   867→}
   868→
   869→[data-theme="dark"] .connection-indicator--disconnected {
   870→  background: var(--color-error-950);
   871→  color: var(--color-error-300);
   872→  border: 1px solid var(--color-error-800);
   873→}
   874→
   875→/* Notification badges */
   876→.notification-badge {
   877→  display: inline-flex;
   878→  align-items: center;
   879→  justify-content: center;
   880→  min-width: 1.25rem;
   881→  height: 1.25rem;
   882→  padding: 0 0.25rem;
   883→  font-size: 0.75rem;
   884→  font-weight: var(--font-weight-bold);
   885→  line-height: 1;
   886→  border-radius: var(--radius-full);
   887→  white-space: nowrap;
   888→}
   889→
   890→.notification-badge--primary {
   891→  background: var(--color-primary-500);
   892→  color: white;
   893→}
   894→
   895→.notification-badge--error {
   896→  background: var(--color-error-500);
   897→  color: white;
   898→}
   899→
   900→.notification-badge--warning {
   901→  background: var(--color-warning-500);
   902→  color: white;
   903→}
   904→
   905→.notification-badge--success {
   906→  background: var(--color-success-500);
   907→  color: white;
   908→}
   909→
   910→/* Performance optimizations */
   911→.will-change-transform {
   912→  will-change: transform;
   913→}
   914→
   915→.will-change-opacity {
   916→  will-change: opacity;
   917→}
   918→
   919→.gpu-acceleration {
   920→  transform: translateZ(0);
   921→  backface-visibility: hidden;
   922→  perspective: 1000;
   923→}
   924→
   925→/* Battery optimization styles */
   926→@media (max-width: 768px) {
   927→  .battery-optimize {
   928→    animation-duration: 0s !important;
   929→    transition-duration: 0.1s !important;
   930→  }
   931→  
   932→  .battery-optimize .glass-medium,
   933→  .battery-optimize .glass-strong {
   934→    backdrop-filter: none !important;
   935→    -webkit-backdrop-filter: none !important;
   936→    background: var(--color-surface-primary) !important;
   937→  }
   938→}
   939→
   940→/* Print styles */
   941→@media print {
   942→  .no-print {
   943→    display: none !important;
   944→  }
   945→  
   946→  .notifications-container,
   947→  .chat-widget-container,
   948→  .connection-indicator {
   949→    display: none !important;
   950→  }
   951→  
   952→  * {
   953→    background: transparent !important;
   954→    color: black !important;
   955→    box-shadow: none !important;
   956→    text-shadow: none !important;
   957→  }
   958→}
   959→
   960→/* ===== MOBILE OPTIMIZATIONS ===== */
   961→
   962→/* Touch optimization styles */
   963→.touch-optimized {
   964→  -webkit-tap-highlight-color: transparent;
   965→  -webkit-touch-callout: none;
   966→  -webkit-user-select: none;
   967→  user-select: none;
   968→}
   969→
   970→.touch-optimized button,
   971→.touch-optimized [role="button"],
   972→.touch-optimized a,
   973→.touch-optimized input,
   974→.touch-optimized select,
   975→.touch-optimized textarea {
   976→  -webkit-user-select: text;
   977→  user-select: text;
   978→}
   979→
   980→/* Large touch targets for mobile */
   981→.large-touch-targets button,
   982→.large-touch-targets [role="button"],
   983→.large-touch-targets a,
   984→.large-touch-targets .btn-base {
   985→  min-height: 44px;
   986→  min-width: 44px;
   987→  padding: var(--spacing-3) var(--spacing-4);
   988→}
   989→
   990→.large-touch-targets .btn-sm {
   991→  min-height: 40px;
   992→  min-width: 40px;
   993→}
   994→
   995→.large-touch-targets .btn-lg {
   996→  min-height: 48px;
   997→  min-width: 48px;
   998→}
   999→
  1000→/* Performance mode optimizations */
  1001→.performance-mode * {
  1002→  will-change: auto !important;
  1003→  animation-duration: 0.1s !important;
  1004→  transition-duration: 0.1s !important;
  1005→}
  1006→
  1007→.performance-mode .glass-medium,
  1008→.performance-mode .glass-strong {
  1009→  backdrop-filter: none !important;
  1010→  -webkit-backdrop-filter: none !important;
  1011→  background: var(--color-surface-primary) !important;
  1012→}
  1013→
  1014→/* Reduced animations for accessibility and battery */
  1015→.reduce-animations * {
  1016→  animation-duration: 0.01ms !important;
  1017→  animation-iteration-count: 1 !important;
  1018→  transition-duration: 0.01ms !important;
  1019→}
  1020→
  1021→.reduce-animations .animate-pulse,
  1022→.reduce-animations .animate-bounce,
  1023→.reduce-animations .animate-spin {
  1024→  animation: none !important;
  1025→}
  1026→
  1027→/* Mobile-specific breakpoints */
  1028→@media (max-width: 480px) {
  1029→  /* Extra small mobile phones */
  1030→  .responsive-mobile-xs {
  1031→    font-size: 14px;
  1032→    padding: var(--spacing-2);
  1033→  }
  1034→  
  1035→  .btn-base {
  1036→    font-size: var(--font-size-sm);
  1037→    padding: var(--spacing-2) var(--spacing-3);
  1038→  }
  1039→  
  1040→  .modal-backdrop {
  1041→    backdrop-filter: none;
  1042→    -webkit-backdrop-filter: none;
  1043→  }
  1044→}
  1045→
  1046→@media (max-width: 640px) {
  1047→  /* Small mobile devices */
  1048→  .responsive-mobile-sm {
  1049→    padding: var(--spacing-3);
  1050→  }
  1051→  
  1052→  .card-base {
  1053→    border-radius: var(--radius-lg);
  1054→    margin: var(--spacing-2);
  1055→  }
  1056→  
  1057→  .glass-medium,
  1058→  .glass-strong {
  1059→    backdrop-filter: blur(8px) !important;
  1060→    -webkit-backdrop-filter: blur(8px) !important;
  1061→  }
  1062→}
  1063→
  1064→/* Landscape mobile orientation */
  1065→@media (max-height: 500px) and (orientation: landscape) {
  1066→  .landscape-optimize {
  1067→    height: 100vh;
  1068→    overflow-y: auto;
  1069→  }
  1070→  
  1071→  .notifications-container {
  1072→    top: var(--spacing-2);
  1073→    right: var(--spacing-2);
  1074→  }
  1075→  
  1076→  .chat-widget-container {
  1077→    bottom: var(--spacing-2);
  1078→    right: var(--spacing-2);
  1079→  }
  1080→}
  1081→
  1082→/* High DPI displays (retina) */
  1083→@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 2dppx) {
  1084→  .retina-optimize {
  1085→    image-rendering: -webkit-optimize-contrast;
  1086→    image-rendering: crisp-edges;
  1087→  }
  1088→  
  1089→  .retina-optimize img {
  1090→    image-rendering: -webkit-optimize-contrast;
  1091→    image-rendering: crisp-edges;
  1092→  }
  1093→}
  1094→
  1095→/* Hover detection for touch devices */
  1096→@media (hover: none) {
  1097→  .hover-only {
  1098→    display: none !important;
  1099→  }
  1100→  
  1101→  .btn-base:hover {
  1102→    transform: none !important;
  1103→  }
  1104→  
  1105→  .card-base:hover {
  1106→    transform: none !important;
  1107→    box-shadow: var(--shadow-sm) !important;
  1108→  }
  1109→}
  1110→
  1111→/* Pointer device optimization */
  1112→@media (pointer: coarse) {
  1113→  /* Touch devices */
  1114→  .form-control-base {
  1115→    font-size: 16px; /* Prevents zoom on iOS */
  1116→    padding: var(--spacing-3);
  1117→  }
  1118→  
  1119→  .btn-base {
  1120→    padding: var(--spacing-3) var(--spacing-4);
  1121→  }
  1122→  
  1123→  select,
  1124→  input,
  1125→  textarea {
  1126→    font-size: 16px; /* Prevents zoom on iOS */
  1127→  }
  1128→}
  1129→
  1130→/* Safe area insets for devices with notches */
  1131→@supports (padding: max(0px)) {
  1132→  .safe-area-inset-top {
  1133→    padding-top: max(var(--spacing-4), env(safe-area-inset-top));
  1134→  }
  1135→  
  1136→  .safe-area-inset-bottom {
  1137→    padding-bottom: max(var(--spacing-4), env(safe-area-inset-bottom));
  1138→  }
  1139→  
  1140→  .safe-area-inset-left {
  1141→    padding-left: max(var(--spacing-4), env(safe-area-inset-left));
  1142→  }
  1143→  
  1144→  .safe-area-inset-right {
  1145→    padding-right: max(var(--spacing-4), env(safe-area-inset-right));
  1146→  }
  1147→}
  1148→
  1149→/* PWA display modes */
  1150→@media (display-mode: standalone) {
  1151→  .pwa-standalone {
  1152→    padding-top: var(--spacing-4);
  1153→  }
  1154→  
  1155→  .pwa-standalone .navbar {
  1156→    padding-top: env(safe-area-inset-top);
  1157→  }
  1158→}
  1159→
  1160→/* Connection-aware styles */
  1161→.slow-connection .glass-medium,
  1162→.slow-connection .glass-strong {
  1163→  backdrop-filter: none !important;
  1164→  -webkit-backdrop-filter: none !important;
  1165→  background: var(--color-surface-primary) !important;
  1166→}
  1167→
  1168→.slow-connection img {
  1169→  filter: blur(2px);
  1170→  transition: filter var(--transition-base);
  1171→}
  1172→
  1173→.slow-connection img.loaded {
  1174→  filter: none;
  1175→}
  1176→
  1177→/* Gesture feedback */
  1178→.gesture-feedback {
  1179→  position: relative;
  1180→  overflow: hidden;
  1181→}
  1182→
  1183→.gesture-feedback::after {
  1184→  content: '';
  1185→  position: absolute;
  1186→  top: 50%;
  1187→  left: 50%;
  1188→  width: 0;
  1189→  height: 0;
  1190→  border-radius: 50%;
  1191→  background: rgba(0, 0, 0, 0.1);
  1192→  transform: translate(-50%, -50%);
  1193→  transition: width 0.3s ease, height 0.3s ease;
  1194→  pointer-events: none;
  1195→}
  1196→
  1197→.gesture-feedback.active::after {
  1198→  width: 100px;
  1199→  height: 100px;
  1200→}
  1201→
  1202→/* Dark mode gesture feedback */
  1203→[data-theme="dark"] .gesture-feedback::after {
  1204→  background: rgba(255, 255, 255, 0.1);
  1205→}
  1206→
  1207→/* Mobile navigation improvements - removed unused mobile-nav-optimize styles that caused gray bar at bottom */
  1208→
  1209→/* Swipe gesture indicators */
  1210→.swipe-indicator {
  1211→  position: relative;
  1212→}
  1213→
  1214→.swipe-indicator::before {
  1215→  content: '';
  1216→  position: absolute;
  1217→  top: 50%;
  1218→  right: var(--spacing-4);
  1219→  width: 20px;
  1220→  height: 2px;
  1221→  background: var(--color-gray-400);
  1222→  transform: translateY(-50%);
  1223→  opacity: 0.5;
  1224→}
  1225→
  1226→.swipe-indicator::after {
  1227→  content: '';
  1228→  position: absolute;
  1229→  top: 50%;
  1230→  right: var(--spacing-3);
  1231→  width: 0;
  1232→  height: 0;
  1233→  border-left: 6px solid var(--color-gray-400);
  1234→  border-top: 4px solid transparent;
  1235→  border-bottom: 4px solid transparent;
  1236→  transform: translateY(-50%);
  1237→  opacity: 0.5;
  1238→}
  1239→
  1240→/* Loading states optimized for mobile */
  1241→.mobile-loading {
  1242→  display: flex;
  1243→  flex-direction: column;
  1244→  align-items: center;
  1245→  justify-content: center;
  1246→  min-height: 200px;
  1247→  padding: var(--spacing-8);
  1248→}
  1249→
  1250→.mobile-loading .loading-spinner {
  1251→  width: 40px;
  1252→  height: 40px;
  1253→  border: 3px solid var(--color-gray-200);
  1254→  border-top: 3px solid var(--color-primary-500);
  1255→  border-radius: 50%;
  1256→  animation: spin 1s linear infinite;
  1257→}
  1258→
  1259→.mobile-loading .loading-text {
  1260→  margin-top: var(--spacing-4);
  1261→  font-size: var(--font-size-sm);
  1262→  color: var(--color-text-secondary);
  1263→  text-align: center;
  1264→}
  1265→
  1266→/* Battery optimization styles */
  1267→.battery-optimize {
  1268→  animation-play-state: paused !important;
  1269→}
  1270→
  1271→.battery-optimize * {
  1272→  animation-play-state: paused !important;
  1273→  transition-duration: 0.1s !important;
  1274→}
  1275→
  1276→.battery-optimize .glass-medium,
  1277→.battery-optimize .glass-strong {
  1278→  backdrop-filter: none !important;
  1279→  -webkit-backdrop-filter: none !important;
  1280→  background: var(--color-surface-primary) !important;
  1281→}
  1282→
  1283→.battery-optimize .animate-pulse,
  1284→.battery-optimize .animate-bounce,
  1285→.battery-optimize .animate-spin {
  1286→  animation: none !important;
  1287→}
  1288→
  1289→/* Accessibility enhancements for mobile */
  1290→@media (max-width: 768px) {
  1291→  .mobile-a11y button,
  1292→  .mobile-a11y [role="button"],
  1293→  .mobile-a11y input,
  1294→  .mobile-a11y select {
  1295→    min-height: 44px;
  1296→    font-size: 16px;
  1297→  }
  1298→  
  1299→  .mobile-a11y .focus-ring:focus-visible {
  1300→    outline: 3px solid var(--color-primary-500);
  1301→    outline-offset: 2px;
  1302→  }
  1303→}

