   715→        "xs": {
   716→          "$type": "dimension",
   717→          "$value": "{spacing.unit} * 2"
   718→        },
   719→        "sm": {
   720→          "$type": "dimension",
   721→          "$value": "{spacing.unit} * 4"
   722→        }
   723→      }
   724→    },
   725→    "motion": {
   726→      "duration": {
   727→        "fast": {
   728→          "$type": "duration",
   729→          "$value": "200ms"
   730→        },
   731→        "normal": {
   732→          "$type": "duration",
   733→          "$value": "300ms"
   734→        }
   735→      },
   736→      "easing": {
