  padding: var(--spacing-2) var(--spacing-4);
  background-color: var(--color-primary-600);
  color: #ffffff;
  border-radius: var(--radius-md);
  font-size: var(--font-size-sm);
  font-weight: var(--font-weight-medium);
  text-decoration: none;

  &:focus,
  &:focus-visible {
    left: var(--spacing-4);
    top: var(--spacing-4);
    outline: none;
    box-shadow: var(--shadow-focus-primary);
  }
}

/* ==========================================
 * PERFORMANCE & OPTIMIZATION UTILITIES
 * ========================================== */

@utility scrollbar-hidden {
  scrollbar-width: none;

  &::-webkit-scrollbar {
    display: none;
  }
}

@utility text-balance {
  text-wrap: balance;
}

@utility content-auto {
  content-visibility: auto;
}

@utility gpu-accelerate {
  transform: translateZ(0);
  backface-visibility: hidden;
  perspective: 1000px;
}

/* ==========================================
 * CONTAINER QUERY UTILITIES
 * ========================================== */

@utility container-inline {
  container-type: inline-size;
}

@utility container-normal {
  container-type: normal;
}

@utility container-size {
  container-type: size;
}

/* Named container contexts for specific use cases */
@utility container-card {
  container-type: inline-size;
  container-name: card;
}

@utility container-widget {
  container-type: inline-size;
  container-name: widget;
}

@utility container-section {
  container-type: inline-size;
  container-name: section;
}

@utility container-modal {
  container-type: inline-size;
  container-name: modal;
}

