   440→  background: var(--gradient-feature-card);
   441→}
   442→
   443→/* CTA/Interactive Gradients with Glow Effects */
   444→.bg-gradient-cta {
   445→  background: var(--gradient-cta);
   446→  box-shadow: 0 0 20px rgba(0, 124, 137, 0.3);
   447→  transition: all 0.3s ease;
   448→}
   449→
   450→.bg-gradient-cta:hover {
   451→  background: var(--gradient-cta-hover);
   452→  box-shadow: 0 0 40px rgba(0, 124, 137, 0.5);
   453→  transform: translateY(-2px);
   454→}
   455→
   456→.bg-gradient-cta:active {
   457→  transform: translateY(0);
   458→  box-shadow: 0 0 30px rgba(0, 124, 137, 0.4);
   459→}
   460→
   461→/* Mesh/Complex Gradients */
   462→.bg-gradient-mesh {
   463→  background: var(--gradient-mesh);
   464→}
   465→
   466→/* Optional Mesh Overlay - Add subtle depth to any element */
   467→.bg-gradient-mesh-overlay {
   468→  position: relative;
   469→}
   470→
   471→.bg-gradient-mesh-overlay::before {
   472→  content: '';
   473→  position: absolute;
   474→  inset: 0;
   475→  background: var(--gradient-mesh-overlay);
   476→  pointer-events: none;
   477→  z-index: 1;
   478→}
   479→
   480→/* Text Gradients - utility classes for gradient text */
   481→.text-gradient-primary {
   482→  background: var(--gradient-accent-primary);
   483→  -webkit-background-clip: text;
   484→  background-clip: text;
   485→  -webkit-text-fill-color: transparent;
   486→  color: transparent; /* Fallback */
   487→}
   488→
   489→.text-gradient-purple {
   490→  background: var(--gradient-accent-purple);
   491→  -webkit-background-clip: text;
   492→  background-clip: text;
   493→  -webkit-text-fill-color: transparent;
   494→  color: transparent; /* Fallback */
   495→}
   496→
   497→.text-gradient-warm {
   498→  background: var(--gradient-accent-warm);
   499→  -webkit-background-clip: text;
   500→  background-clip: text;
   501→  -webkit-text-fill-color: transparent;
   502→  color: transparent; /* Fallback */
   503→}
   504→
   505→/* Animated Gradients */
   506→@keyframes gradientShift {
   507→  0%, 100% {
   508→    background-position: 0% 50%;
   509→  }
   510→  50% {
   511→    background-position: 100% 50%;
   512→  }
   513→}
   514→
   515→.bg-gradient-animated {
   516→  background-size: 200% 200%;
   517→  animation: gradientShift 8s ease infinite;
   518→}
   519→
   520→  /* Responsive Gradient Utilities */
   521→  @media (max-width: 768px) {
