  1029→    padding: var(--spacing-3);
  1030→  }
  1031→  
  1032→  .card-base {
  1033→    border-radius: var(--radius-lg);
  1034→    margin: var(--spacing-2);
  1035→  }
  1036→  
  1037→  .glass-medium,
  1038→  .glass-strong {
  1039→    backdrop-filter: blur(8px) !important;
  1040→    -webkit-backdrop-filter: blur(8px) !important;
  1041→  }
  1042→}
  1043→
  1044→/* Landscape mobile orientation */
  1045→@media (max-height: 500px) and (orientation: landscape) {
  1046→  .landscape-optimize {
  1047→    height: 100vh;
  1048→    overflow-y: auto;
  1049→  }
  1050→  
  1051→  .notifications-container {
  1052→    top: var(--spacing-2);
  1053→    right: var(--spacing-2);
  1054→  }
  1055→  
  1056→  .chat-widget-container {
  1057→    bottom: var(--spacing-2);
  1058→    right: var(--spacing-2);
  1059→  }
  1060→}
  1061→
  1062→/* High DPI displays (retina) */
  1063→@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 2dppx) {
  1064→  .retina-optimize {
  1065→    image-rendering: -webkit-optimize-contrast;
  1066→    image-rendering: crisp-edges;
  1067→  }
  1068→  
  1069→  .retina-optimize img {
  1070→    image-rendering: -webkit-optimize-contrast;
  1071→    image-rendering: crisp-edges;
  1072→  }
  1073→}
  1074→
  1075→/* Hover detection for touch devices */
  1076→@media (hover: none) {
  1077→  .hover-only {
  1078→    display: none !important;
  1079→  }
  1080→  
  1081→  .btn-base:hover {
  1082→    transform: none !important;
  1083→  }
  1084→  
  1085→  .card-base:hover {
  1086→    transform: none !important;
  1087→    box-shadow: var(--shadow-sm) !important;
  1088→  }
  1089→}
  1090→
  1091→/* Pointer device optimization */
  1092→@media (pointer: coarse) {
  1093→  /* Touch devices */
  1094→  .form-control-base {
  1095→    font-size: 16px; /* Prevents zoom on iOS */
  1096→    padding: var(--spacing-3);
  1097→  }
  1098→  
  1099→  .btn-base {
  1100→    padding: var(--spacing-3) var(--spacing-4);
  1101→  }
  1102→  
  1103→  select,
  1104→  input,
  1105→  textarea {
  1106→    font-size: 16px; /* Prevents zoom on iOS */
  1107→  }
  1108→}
  1109→
  1110→/* Safe area insets for devices with notches */
  1111→@supports (padding: max(0px)) {
  1112→  .safe-area-inset-top {
  1113→    padding-top: max(var(--spacing-4), env(safe-area-inset-top));
  1114→  }
  1115→  
  1116→  .safe-area-inset-bottom {
  1117→    padding-bottom: max(var(--spacing-4), env(safe-area-inset-bottom));
  1118→  }
  1119→  
  1120→  .safe-area-inset-left {
  1121→    padding-left: max(var(--spacing-4), env(safe-area-inset-left));
  1122→  }
  1123→  
  1124→  .safe-area-inset-right {
  1125→    padding-right: max(var(--spacing-4), env(safe-area-inset-right));
  1126→  }
  1127→}
  1128→
  1129→/* PWA display modes */
  1130→@media (display-mode: standalone) {
  1131→  .pwa-standalone {
  1132→    padding-top: var(--spacing-4);
  1133→  }
  1134→  
  1135→  .pwa-standalone .navbar {
  1136→    padding-top: env(safe-area-inset-top);
  1137→  }
  1138→}
  1139→
  1140→/* Connection-aware styles */
  1141→.slow-connection .glass-medium,
  1142→.slow-connection .glass-strong {
  1143→  backdrop-filter: none !important;
  1144→  -webkit-backdrop-filter: none !important;
  1145→  background: var(--color-surface-primary) !important;
  1146→}
  1147→
  1148→.slow-connection img {
  1149→  filter: blur(2px);
  1150→  transition: filter var(--transition-base);
  1151→}
  1152→
  1153→.slow-connection img.loaded {
  1154→  filter: none;
  1155→}
  1156→
  1157→/* Gesture feedback */
  1158→.gesture-feedback {
  1159→  position: relative;
  1160→  overflow: hidden;
  1161→}
  1162→
  1163→.gesture-feedback::after {
  1164→  content: '';
  1165→  position: absolute;
  1166→  top: 50%;
  1167→  left: 50%;
  1168→  width: 0;
  1169→  height: 0;
  1170→  border-radius: 50%;
  1171→  background: rgba(0, 0, 0, 0.1);
  1172→  transform: translate(-50%, -50%);
  1173→  transition: width 0.3s ease, height 0.3s ease;
  1174→  pointer-events: none;
  1175→}
  1176→
  1177→.gesture-feedback.active::after {
  1178→  width: 100px;
  1179→  height: 100px;
  1180→}
  1181→
  1182→/* Dark mode gesture feedback */
  1183→[data-theme="dark"] .gesture-feedback::after {
  1184→  background: rgba(255, 255, 255, 0.1);
  1185→}
  1186→
  1187→/* Mobile navigation improvements - removed unused mobile-nav-optimize styles that caused gray bar at bottom */
  1188→
  1189→/* Swipe gesture indicators */
  1190→.swipe-indicator {
  1191→  position: relative;
  1192→}
  1193→
  1194→.swipe-indicator::before {
  1195→  content: '';
  1196→  position: absolute;
  1197→  top: 50%;
  1198→  right: var(--spacing-4);
  1199→  width: 20px;
  1200→  height: 2px;
  1201→  background: var(--color-gray-400);
  1202→  transform: translateY(-50%);
  1203→  opacity: 0.5;
  1204→}
  1205→
  1206→.swipe-indicator::after {
  1207→  content: '';
  1208→  position: absolute;
  1209→  top: 50%;
  1210→  right: var(--spacing-3);
  1211→  width: 0;
  1212→  height: 0;
  1213→  border-left: 6px solid var(--color-gray-400);
  1214→  border-top: 4px solid transparent;
  1215→  border-bottom: 4px solid transparent;
  1216→  transform: translateY(-50%);
  1217→  opacity: 0.5;
  1218→}
  1219→
  1220→/* Loading states optimized for mobile */
  1221→.mobile-loading {
  1222→  display: flex;
  1223→  flex-direction: column;
  1224→  align-items: center;
  1225→  justify-content: center;
  1226→  min-height: 200px;
  1227→  padding: var(--spacing-8);
  1228→}
  1229→
  1230→.mobile-loading .loading-spinner {
  1231→  width: 40px;
  1232→  height: 40px;
  1233→  border: 3px solid var(--color-gray-200);
  1234→  border-top: 3px solid var(--color-primary-500);
  1235→  border-radius: 50%;
  1236→  animation: spin 1s linear infinite;
  1237→}
  1238→
  1239→.mobile-loading .loading-text {
  1240→  margin-top: var(--spacing-4);
  1241→  font-size: var(--font-size-sm);
  1242→  color: var(--color-text-secondary);
  1243→  text-align: center;
  1244→}
  1245→
  1246→/* Battery optimization styles */
  1247→.battery-optimize {
  1248→  animation-play-state: paused !important;
  1249→}
  1250→
  1251→.battery-optimize * {
  1252→  animation-play-state: paused !important;
  1253→  transition-duration: 0.1s !important;
  1254→}
  1255→
  1256→.battery-optimize .glass-medium,
  1257→.battery-optimize .glass-strong {
  1258→  backdrop-filter: none !important;
  1259→  -webkit-backdrop-filter: none !important;
  1260→  background: var(--color-surface-primary) !important;
  1261→}
  1262→
  1263→.battery-optimize .animate-pulse,
  1264→.battery-optimize .animate-bounce,
  1265→.battery-optimize .animate-spin {
  1266→  animation: none !important;
  1267→}
  1268→
  1269→/* Accessibility enhancements for mobile */
  1270→@media (max-width: 768px) {
  1271→  .mobile-a11y button,
  1272→  .mobile-a11y [role="button"],
  1273→  .mobile-a11y input,
  1274→  .mobile-a11y select {
  1275→    min-height: 44px;
  1276→    font-size: 16px;
  1277→  }
  1278→  
  1279→  .mobile-a11y .focus-ring:focus-visible {
  1280→    outline: 3px solid var(--color-primary-500);
  1281→    outline-offset: 2px;
  1282→  }
  1283→}

