     1→/**
     2→ * Socialaize Design System Foundation
     3→ * 
     4→ * A comprehensive design token system for consistent theming,
     5→ * components, and user experiences across the platform.
     6→ */
     7→
     8→:root {
     9→  /* ===== COLOR PALETTE ===== */
    10→
    11→  /* Primary Brand Colors - OKLCH for perceptual uniformity and better dark mode */
    12→  --color-primary-50: oklch(95% 0.03 200);
    13→  --color-primary-100: oklch(90% 0.05 200);
    14→  --color-primary-200: oklch(80% 0.08 200);
    15→  --color-primary-300: oklch(70% 0.11 200);
    16→  --color-primary-400: oklch(60% 0.14 200);
    17→  --color-primary-500: oklch(50% 0.16 200);  /* Base: #007c89 */
    18→  --color-primary-600: oklch(45% 0.14 200);
    19→  --color-primary-700: oklch(35% 0.11 200);
    20→  --color-primary-800: oklch(25% 0.08 200);
    21→  --color-primary-900: oklch(15% 0.05 200);
    22→  --color-primary-950: oklch(10% 0.03 200);
    23→
    24→  /* Semantic Colors - Success (OKLCH) */
    25→  --color-success-50: oklch(98% 0.03 160);
