  }

  [data-theme="dark"] .glass-navbar {
    background: rgba(17, 24, 39, 0.75);
    border-bottom: 1px solid rgba(55, 65, 81, 0.3);
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
  }

  @media (prefers-reduced-motion: reduce) {
    .glass-navbar {
      backdrop-filter: none;
      -webkit-backdrop-filter: none;
    }
  }

  @media (prefers-reduced-transparency: reduce) {
    .glass-navbar {
      backdrop-filter: none;
      -webkit-backdrop-filter: none;
      background: rgba(255, 255, 255, 0.98);
    }

    [data-theme="dark"] .glass-navbar {
      background: rgba(17, 24, 39, 0.98);
    }
  }

  @media (prefers-reduced-data: reduce) {
    .glass-navbar {
      backdrop-filter: none;
      -webkit-backdrop-filter: none;
      background: rgba(255, 255, 255, 0.98);
    }

    [data-theme="dark"] .glass-navbar {
      background: rgba(17, 24, 39, 0.98);
    }
  }

  .glass-modal {
    background: rgba(255, 255, 255, 0.92);
    backdrop-filter: blur(16px);
    -webkit-backdrop-filter: blur(16px);
    border: 1px solid rgba(255, 255, 255, 0.35);
    border-radius: var(--radius-2xl);
    box-shadow: var(--shadow-2xl);
  }

  [data-theme="dark"] .glass-modal {
    background: rgba(17, 24, 39, 0.92);
    border: 1px solid rgba(55, 65, 81, 0.45);
  }

  @media (prefers-reduced-motion: reduce) {
    .glass-modal {
      backdrop-filter: none;
      -webkit-backdrop-filter: none;
    }
  }

  @media (prefers-reduced-transparency: reduce) {
    .glass-modal {
      backdrop-filter: none;
      -webkit-backdrop-filter: none;
      background: rgba(255, 255, 255, 0.98);
    }

    [data-theme="dark"] .glass-modal {
      background: rgba(17, 24, 39, 0.98);
    }
  }

  @media (prefers-reduced-data: reduce) {
    .glass-modal {
      backdrop-filter: none;
      -webkit-backdrop-filter: none;
      background: rgba(255, 255, 255, 0.98);
    }

    [data-theme="dark"] .glass-modal {
      background: rgba(17, 24, 39, 0.98);
    }
  }

  /* ==========================================
   * FOCUS RING UTILITIES
   * ========================================== */

  .focus-ring:focus,
  .focus-ring:focus-visible {
    outline: none;
    box-shadow: var(--shadow-focus-primary);
    border-color: var(--color-primary-500);
  }

  @media (prefers-contrast: high) {
    .focus-ring:focus,
    .focus-ring:focus-visible {
      outline: 2px solid var(--color-primary-600);
      outline-offset: 2px;
    }
  }

  .focus-ring-error:focus,
  .focus-ring-error:focus-visible {
    outline: none;
    box-shadow: var(--shadow-focus-error);
    border-color: var(--color-error-500);
  }

  @media (prefers-contrast: high) {
    .focus-ring-error:focus,
    .focus-ring-error:focus-visible {
      outline: 2px solid var(--color-error-600);
      outline-offset: 2px;
    }
  }

  .focus-ring-success:focus,
  .focus-ring-success:focus-visible {
    outline: none;
    box-shadow: var(--shadow-focus-success);
    border-color: var(--color-success-500);
  }

  @media (prefers-contrast: high) {
    .focus-ring-success:focus,
    .focus-ring-success:focus-visible {
      outline: 2px solid var(--color-success-600);
      outline-offset: 2px;
    }
  }

  .focus-ring-info:focus,
  .focus-ring-info:focus-visible {
    outline: none;
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
    border-color: var(--color-info-500);
  }

  @media (prefers-contrast: high) {
    .focus-ring-info:focus,
    .focus-ring-info:focus-visible {
      outline: 2px solid var(--color-info-600);
      outline-offset: 2px;
    }
  }

  .focus-ring-warning:focus,
  .focus-ring-warning:focus-visible {
    outline: none;
    box-shadow: 0 0 0 3px rgba(245, 158, 11, 0.1);
    border-color: var(--color-warning-500);
  }

  @media (prefers-contrast: high) {
    .focus-ring-warning:focus,
    .focus-ring-warning:focus-visible {
      outline: 2px solid var(--color-warning-600);
      outline-offset: 2px;
    }
  }

  .focus-ring-offset:focus,
  .focus-ring-offset:focus-visible {
    outline: none;
    box-shadow: var(--shadow-focus-primary), 0 0 0 4px rgba(255, 255, 255, 0.1);
  }

  [data-theme="dark"] .focus-ring-offset:focus,
  [data-theme="dark"] .focus-ring-offset:focus-visible {
    box-shadow: var(--shadow-focus-primary), 0 0 0 4px rgba(0, 0, 0, 0.2);
  }

  .focus-ring-inset:focus,
  .focus-ring-inset:focus-visible {
    outline: none;
    box-shadow: inset 0 0 0 2px var(--color-primary-500);
  }

  .focus-visible-only:focus:not(:focus-visible) {
    outline: none;
    box-shadow: none;
  }

  .focus-visible-only:focus-visible {
    outline: none;
    box-shadow: var(--shadow-focus-primary);
  }

  .focus-within-ring:focus-within {
    outline: none;
    box-shadow: var(--shadow-focus-primary);
  }

  .focus-none:focus,
  .focus-none:focus-visible {
    outline: none;
    box-shadow: none;
  }

  /* ==========================================
   * ACCESSIBILITY UTILITIES
   * ========================================== */

  .skip-link {
    position: absolute;
    left: -9999px;
    z-index: 9999;
    padding: var(--spacing-2) var(--spacing-4);
    background-color: var(--color-primary-600);
    color: #ffffff;
    border-radius: var(--radius-md);
    font-size: var(--font-size-sm);
    font-weight: var(--font-weight-medium);
    text-decoration: none;
  }

  .skip-link:focus,
  .skip-link:focus-visible {
    left: var(--spacing-4);
    top: var(--spacing-4);
    outline: none;
    box-shadow: var(--shadow-focus-primary);
  }

  /* ==========================================
   * PERFORMANCE & OPTIMIZATION UTILITIES
   * ========================================== */

  .scrollbar-hidden {
    scrollbar-width: none;
  }

  .scrollbar-hidden::-webkit-scrollbar {
    display: none;
  }

  .text-balance {
    text-wrap: balance;
  }

  .content-auto {
    content-visibility: auto;
  }

  .gpu-accelerate {
    transform: translateZ(0);
    backface-visibility: hidden;
    perspective: 1000px;
  }

  /* ==========================================
   * CONTAINER QUERY UTILITIES
   * ========================================== */

  .container-inline {
    container-type: inline-size;
  }

  .container-normal {
    container-type: normal;
  }

  .container-size {
    container-type: size;
  }

  /* Named container contexts for specific use cases */
  .container-card {
    container-type: inline-size;
    container-name: card;
  }

  .container-widget {
    container-type: inline-size;
    container-name: widget;
  }

  .container-section {
    container-type: inline-size;
    container-name: section;
  }

  .container-modal {
    container-type: inline-size;
    container-name: modal;
  }
}

