[data-theme="dark"] {
  --surface-primary: #1a1a1a;
  --surface-secondary: #2d2d2d;
  --text-primary: #ffffff;
  --text-secondary: #b0b0b0;
  --border-subtle: #404040;
}

/* Or use prefers-color-scheme media query */
@media (prefers-color-scheme: dark) {
  :root {
    --surface-primary: #1a1a1a;
    --surface-secondary: #2d2d2d;
    --text-primary: #ffffff;
    --text-secondary: #b0b0b0;
  }
}
