.responsive-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
  gap: 24px;
  padding: 24px;
}

/* Auto-fill variant (creates empty tracks) */
.auto-fill-grid {
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
}

/* With max-width constraint */
.constrained-grid {
  max-width: 1400px;
  margin: 0 auto;
}
