/* Pulsing animation for online status */
.status-dot--online::after {
  content: '';
  position: absolute;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  border: 2px solid var(--success-500);
  opacity: 0;
  animation: pulse 2s ease-in-out infinite;
