  }

  .card-subtitle {
    font-size: var(--font-size-sm);
    color: var(--color-text-secondary);
    margin: 0;
  }

  .card-description {
    font-size: var(--font-size-base);
    color: var(--color-text-secondary);
    line-height: var(--line-height-relaxed);
    margin: 0;
  }

  .card-footer {
    padding: var(--spacing-4) var(--spacing-6);
    display: flex;
    gap: var(--spacing-3);
    border-top: 1px solid var(--color-border-primary);
  }

  /* Avatar Component */
  .avatar {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    border-radius: var(--radius-full);
    background-color: var(--color-primary-100);
    color: var(--color-primary-700);
    font-weight: var(--font-weight-medium);
    flex-shrink: 0;
    overflow: hidden;
  }

  .avatar-xs {
    width: 1.5rem;
    height: 1.5rem;
    font-size: var(--font-size-xs);
  }

  .avatar-sm {
    width: 2rem;
    height: 2rem;
    font-size: var(--font-size-sm);
  }

  .avatar-md {
    width: 2.5rem;
    height: 2.5rem;
    font-size: var(--font-size-base);
  }

  .avatar-lg {
    width: 3rem;
    height: 3rem;
    font-size: var(--font-size-lg);
  }

  .avatar-xl {
    width: 4rem;
    height: 4rem;
    font-size: var(--font-size-xl);
  }

  .avatar img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }

  [data-theme="dark"] .avatar {
    background-color: var(--color-primary-900);
    color: var(--color-primary-300);
  }

  /* List Patterns */
  .list-base {
    list-style: none;
    padding: 0;
    margin: 0;
  }

  .list-item {
    display: flex;
    align-items: center;
    gap: var(--spacing-3);
    padding: var(--spacing-3) var(--spacing-4);
    border-radius: var(--radius-lg);
    transition: background-color 150ms ease;
  }

  .list-item:hover {
    background-color: var(--color-surface-secondary);
  }

  .list-item-interactive {
    cursor: pointer;
  }

  .list-item-interactive:active {
    background-color: var(--color-surface-tertiary);
  }

  .list-item-avatar {
    display: flex;
    align-items: center;
    gap: var(--spacing-3);
  }

  .list-item-content {
    flex: 1;
    min-width: 0;
  }

  .list-item-title {
    font-size: var(--font-size-base);
    font-weight: var(--font-weight-medium);
    color: var(--color-text-primary);
    margin: 0;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  .list-item-subtitle {
    font-size: var(--font-size-sm);
    color: var(--color-text-secondary);
    margin: 0;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
  }

  .list-item-actions {
    display: flex;
    gap: var(--spacing-2);
    align-items: center;
  }

  /* Dividers */
  .divider {
    border: none;
    border-top: 1px solid var(--color-border-primary);
    margin: var(--spacing-6) 0;
  }

  .divider-dashed {
    border-top-style: dashed;
  }

  .divider-label {
    display: flex;
    align-items: center;
    text-align: center;
    margin: var(--spacing-6) 0;
  }

  .divider-label::before,
  .divider-label::after {
    content: '';
    flex: 1;
    border-top: 1px solid var(--color-border-primary);
  }

  .divider-label span {
    padding: 0 var(--spacing-4);
    font-size: var(--font-size-sm);
    color: var(--color-text-tertiary);
    font-weight: var(--font-weight-medium);
  }

  /* Empty State */
  .empty-state {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: var(--spacing-12) var(--spacing-6);
    text-align: center;
    min-height: 20rem;
  }

  .empty-state-icon {
    width: 4rem;
    height: 4rem;
    color: var(--color-text-tertiary);
    margin-bottom: var(--spacing-4);
  }

  .empty-state-title {
    font-size: var(--font-size-xl);
    font-weight: var(--font-weight-semibold);
    color: var(--color-text-primary);
    margin: 0 0 var(--spacing-2);
  }

  .empty-state-description {
    font-size: var(--font-size-base);
    color: var(--color-text-secondary);
    max-width: 32rem;
    margin: 0 0 var(--spacing-6);
  }

  .empty-state-actions {
    display: flex;
    gap: var(--spacing-3);
  }

  /* Pagination */
  .pagination {
    display: flex;
    align-items: center;
    gap: var(--spacing-2);
    list-style: none;
    padding: 0;
    margin: 0;
  }

  .pagination-item {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    min-width: 2.5rem;
    height: 2.5rem;
    padding: 0 var(--spacing-2);
    border-radius: var(--radius-lg);
    font-size: var(--font-size-sm);
    font-weight: var(--font-weight-medium);
    color: var(--color-text-primary);
    background-color: transparent;
    cursor: pointer;
    transition: all 150ms ease;
    text-decoration: none;
  }

  .pagination-item:hover:not(.pagination-item-active):not(.pagination-item-disabled) {
    background-color: var(--color-surface-secondary);
  }

  .pagination-item-active {
    background-color: var(--color-primary-500);
    color: white;
    cursor: default;
  }

  .pagination-item-disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }

  /* Breadcrumbs */
  .breadcrumbs {
    display: flex;
    align-items: center;
    gap: var(--spacing-2);
    list-style: none;
    padding: 0;
    margin: 0;
    flex-wrap: wrap;
  }

  .breadcrumb-item {
    display: flex;
    align-items: center;
    gap: var(--spacing-2);
    font-size: var(--font-size-sm);
  }

  .breadcrumb-link {
    color: var(--color-text-secondary);
    text-decoration: none;
    transition: color 150ms ease;
  }

  .breadcrumb-link:hover {
    color: var(--color-primary-500);
  }

  .breadcrumb-separator {
    color: var(--color-text-tertiary);
  }

  .breadcrumb-item:last-child .breadcrumb-link {
    color: var(--color-text-primary);
    font-weight: var(--font-weight-medium);
    pointer-events: none;
  }

  /* Status Indicator */
  .status-indicator {
    display: inline-flex;
    align-items: center;
    gap: var(--spacing-2);
    font-size: var(--font-size-sm);
    font-weight: var(--font-weight-medium);
  }

  .status-dot {
    width: 0.5rem;
    height: 0.5rem;
    border-radius: var(--radius-full);
    flex-shrink: 0;
  }

  .status-dot-online {
    background-color: var(--color-success-500);
  }

  .status-dot-offline {
    background-color: var(--color-gray-400);
  }

  .status-dot-busy {
    background-color: var(--color-error-500);
  }

  .status-dot-away {
    background-color: var(--color-warning-500);
  }
}

