  EASE_OUT: 'cubic-bezier(0.16, 1, 0.3, 1)',      // Most common
  BOUNCE: 'cubic-bezier(0.68, -0.55, 0.265, 1.55)', // Playful
  SMOOTH: 'cubic-bezier(0.25, 0.46, 0.45, 0.94)',   // Ultra-smooth
