  [data-theme="dark"] .spinner {
    border-color: var(--color-gray-700);
    border-top-color: var(--color-primary-400);
  }

  /* Dots Spinner */
  .spinner-dots {
    display: inline-flex;
    gap: 0.5rem;
  }

  .spinner-dot {
    width: 0.5rem;
    height: 0.5rem;
    background-color: var(--color-primary-500);
    border-radius: var(--radius-full);
    animation: spinner-dot-bounce 1.4s infinite ease-in-out both;
  }

  .spinner-dot:nth-child(1) {
    animation-delay: -0.32s;
  }

  .spinner-dot:nth-child(2) {
    animation-delay: -0.16s;
  }

  @keyframes spinner-dot-bounce {
    0%, 80%, 100% {
      transform: scale(0.8);
      opacity: 0.5;
    }
    40% {
      transform: scale(1.2);
      opacity: 1;
    }
  }

  /* Progress Bar */
  .progress-bar {
    width: 100%;
    height: 0.5rem;
    background-color: var(--color-gray-200);
    border-radius: var(--radius-full);
    overflow: hidden;
  }

  .progress-bar-fill {
    height: 100%;
    background-color: var(--color-primary-500);
    border-radius: var(--radius-full);
    transition: width 300ms ease-out;
  }

  .progress-bar-indeterminate .progress-bar-fill {
    width: 30%;
    animation: progress-indeterminate 1.5s ease-in-out infinite;
  }

  @keyframes progress-indeterminate {
    0% {
      transform: translateX(-100%);
    }
    100% {
      transform: translateX(400%);
    }
  }

  [data-theme="dark"] .progress-bar {
    background-color: var(--color-gray-700);
  }

  /* Button Loading State */
  .btn-loading {
    position: relative;
    color: transparent !important;
    pointer-events: none;
  }

  .btn-loading::after {
    content: '';
    position: absolute;
    width: 1rem;
    height: 1rem;
    top: 50%;
    left: 50%;
    margin-left: -0.5rem;
    margin-top: -0.5rem;
    border: 2px solid currentColor;
    border-top-color: transparent;
    border-radius: var(--radius-full);
    animation: spinner-spin 0.6s linear infinite;
  }

  /* Toast Notifications */
  .toast {
    position: fixed;
    z-index: var(--z-index-toast);
    padding: var(--spacing-4) var(--spacing-6);
    background-color: var(--color-surface-primary);
    border: 1px solid var(--color-border-primary);
    border-radius: var(--radius-xl);
    box-shadow: var(--shadow-xl);
    display: flex;
    align-items: center;
    gap: var(--spacing-3);
    max-width: 24rem;
    animation: toast-slide-in 300ms ease-out;
  }

  @keyframes toast-slide-in {
    from {
      transform: translateY(-100%);
      opacity: 0;
    }
    to {
      transform: translateY(0);
      opacity: 1;
    }
  }

  .toast-top-right {
    top: var(--spacing-4);
    right: var(--spacing-4);
  }

  .toast-top-center {
    top: var(--spacing-4);
    left: 50%;
    transform: translateX(-50%);
  }

  .toast-bottom-right {
    bottom: var(--spacing-4);
    right: var(--spacing-4);
  }

  .toast-success {
    border-left: 4px solid var(--color-success-500);
  }

  .toast-error {
    border-left: 4px solid var(--color-error-500);
  }

  .toast-warning {
    border-left: 4px solid var(--color-warning-500);
  }

  .toast-info {
    border-left: 4px solid var(--color-info-500);
  }

  [data-theme="dark"] .toast {
    background-color: var(--color-dark-surface-primary);
    border-color: var(--color-dark-border-primary);
  }

  /* Success Checkmark Animation */
  .success-checkmark {
    width: 3rem;
    height: 3rem;
    border-radius: var(--radius-full);
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: var(--color-success-500);
    animation: success-scale 0.3s ease-out;
  }

  .success-checkmark::before {
    content: '';
    width: 0.5rem;
    height: 1rem;
    border: solid white;
    border-width: 0 3px 3px 0;
    transform: rotate(45deg);
    animation: success-checkmark-draw 0.3s ease-out 0.1s both;
  }

  @keyframes success-scale {
    0% {
      transform: scale(0);
    }
    50% {
      transform: scale(1.1);
    }
    100% {
      transform: scale(1);
    }
  }

  @keyframes success-checkmark-draw {
    0% {
      height: 0;
    }
    100% {
      height: 1rem;
    }
  }

  /* Error Shake Animation */
  .error-shake {
    animation: error-shake 0.5s ease-in-out;
  }

  @keyframes error-shake {
    0%, 100% {
      transform: translateX(0);
    }
    10%, 30%, 50%, 70%, 90% {
      transform: translateX(-8px);
    }
    20%, 40%, 60%, 80% {
      transform: translateX(8px);
    }
  }

  /* Ripple Effect */
  .ripple-container {
    position: relative;
    overflow: hidden;
  }

  .ripple {
    position: absolute;
    border-radius: var(--radius-full);
    background-color: rgba(255, 255, 255, 0.6);
    transform: scale(0);
    animation: ripple-effect 0.6s ease-out;
    pointer-events: none;
  }

  @keyframes ripple-effect {
    to {
      transform: scale(4);
      opacity: 0;
    }
  }

  [data-theme="dark"] .ripple {
    background-color: rgba(255, 255, 255, 0.2);
  }

  /* Pulse Animation for Badges */
  .pulse-badge {
    position: relative;
  }

  .pulse-badge::before {
    content: '';
    position: absolute;
    inset: -4px;
    border-radius: inherit;
    background-color: currentColor;
    opacity: 0.3;
    animation: pulse-ring 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  @keyframes pulse-ring {
    0%, 100% {
      opacity: 0.3;
      transform: scale(1);
    }
    50% {
      opacity: 0;
      transform: scale(1.5);
    }
  }

  /* Number Counter Animation */
  .counter-animate {
    animation: counter-pop 0.3s ease-out;
  }

  @keyframes counter-pop {
    0% {
      transform: scale(1);
    }
    50% {
      transform: scale(1.2);
    }
    100% {
      transform: scale(1);
    }
  }

  /* Heart/Like Animation */
  .heart-animate {
    animation: heart-bounce 0.3s ease-out;
  }

  @keyframes heart-bounce {
    0% {
      transform: scale(1);
    }
    25% {
      transform: scale(1.3);
    }
    50% {
      transform: scale(0.9);
    }
    75% {
      transform: scale(1.1);
    }
    100% {
      transform: scale(1);
    }
  }

  /* Card Hover Lift */
  .card-lift {
    transition: transform 200ms ease-out, box-shadow 200ms ease-out;
  }

  .card-lift:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-lg);
  }

  /* Link Underline Animation */
  .link-underline {
    position: relative;
    text-decoration: none;
  }

  .link-underline::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 2px;
    background-color: currentColor;
    transform: scaleX(0);
    transform-origin: right;
    transition: transform 300ms ease-out;
  }

  .link-underline:hover::after {
    transform: scaleX(1);
    transform-origin: left;
  }

  /* Focus Ring - Keyboard Only */
  .focus-ring-keyboard {
    outline: 2px solid transparent;
    outline-offset: 2px;
  }

  .focus-ring-keyboard:focus {
    outline: none;
  }

  .focus-ring-keyboard:focus-visible {
    outline: 2px solid var(--color-primary-500);
    outline-offset: 2px;
  }

  /* Bounce Animation */
  .bounce-on-hover {
    transition: transform 200ms cubic-bezier(0.68, -0.55, 0.265, 1.55);
  }

  .bounce-on-hover:hover {
    transform: scale(1.1);
  }

  /* Fade In Animations */
  .fade-in {
    animation: fade-in 300ms ease-out;
  }

  @keyframes fade-in {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }

  .fade-in-up {
    animation: fade-in-up 400ms ease-out;
  }

  @keyframes fade-in-up {
    from {
      opacity: 0;
      transform: translateY(20px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  /* Stagger Animations */
  .stagger-item {
    opacity: 0;
    animation: fade-in-up 400ms ease-out forwards;
  }

  .stagger-item:nth-child(1) { animation-delay: 0ms; }
  .stagger-item:nth-child(2) { animation-delay: 100ms; }
  .stagger-item:nth-child(3) { animation-delay: 200ms; }
  .stagger-item:nth-child(4) { animation-delay: 300ms; }
  .stagger-item:nth-child(5) { animation-delay: 400ms; }
  .stagger-item:nth-child(6) { animation-delay: 500ms; }

  /* Accordion Expand/Collapse */
  .accordion-content {
    overflow: hidden;
    transition: max-height 300ms ease-out, opacity 300ms ease-out;
  }

  .accordion-content[aria-hidden="true"] {
    max-height: 0;
    opacity: 0;
  }

  .accordion-content[aria-hidden="false"] {
    max-height: 1000px;
    opacity: 1;
  }

  /* Tooltip */
  .tooltip {
    position: absolute;
    padding: var(--spacing-2) var(--spacing-3);
    background-color: var(--color-gray-900);
    color: white;
    font-size: var(--font-size-sm);
    border-radius: var(--radius-lg);
    white-space: nowrap;
    z-index: var(--z-index-tooltip);
    opacity: 0;
    pointer-events: none;
    transition: opacity 200ms ease-out;
  }

  .tooltip-trigger:hover .tooltip,
  .tooltip-trigger:focus .tooltip {
    opacity: 1;
  }

  [data-theme="dark"] .tooltip {
    background-color: var(--color-gray-100);
    color: var(--color-gray-900);
  }

  /* Image Zoom on Hover */
  .image-zoom-container {
    overflow: hidden;
    border-radius: inherit;
  }

  .image-zoom {
    transition: transform 400ms ease-out;
  }

  .image-zoom-container:hover .image-zoom {
    transform: scale(1.1);
  }

  /* Reduced Motion Support */
  @media (prefers-reduced-motion: reduce) {
    .skeleton,
    .spinner,
    .spinner-dot,
    .progress-bar-fill,
    .btn-loading::after,
    .toast,
    .success-checkmark,
    .success-checkmark::before,
    .error-shake,
    .ripple,
    .pulse-badge::before,
    .counter-animate,
    .heart-animate,
    .card-lift,
    .link-underline::after,
    .bounce-on-hover,
    .fade-in,
    .fade-in-up,
    .stagger-item,
    .accordion-content,
    .tooltip,
    .image-zoom {
      animation: none !important;
      transition: none !important;
    }

    .card-lift:hover {
      transform: none;
    }

    .bounce-on-hover:hover {
      transform: none;
    }

    .image-zoom-container:hover .image-zoom {
      transform: none;
    }
  }
}

