const emit = defineEmits<Emits>()

const isMounted = useMounted()
const elementRef = useTemplateRef('elementRef')
const teleportTarget = computed(() => props.teleportTo ?? '#teleport-layer')
const teleportDisabled = computed(() => props.teleportTo === null || !isMounted.value)

const animation = useAnimation(elementRef, {
  animation: {
    preset: props.preset,
    duration: props.duration,
    easing: props.easing
  },
  autoplay: false,
  respectEnvironment: true
})

const isAnimating = ref(false)

const isAnimationSupported = computed(() => {
  if (animation.animate?.value) return true
  if (typeof window === 'undefined') return false
  return typeof HTMLElement !== 'undefined' && 'animate' in HTMLElement.prototype
})

const waitForAnimation = async (candidate?: Animation | null) => {
  if (!candidate) return
  try {
    await candidate.finished
  } catch {
    // finished rejects on cancel; silence to mirror legacy behavior
  }
}

const isAnimationInstance = (value: unknown): value is Animation => {
  if (!value || typeof value !== 'object') return false
  return 'finished' in (value as Animation)
}

const runTrackedAnimation = async (
  start: () => Promise<void | Animation | undefined> | Animation | void | undefined
) => {
  isAnimating.value = true
  try {
    const result = await start()
    if (isAnimationInstance(result)) {
      await waitForAnimation(result)
      return
    }
    await waitForAnimation(animation.animate?.value ?? null)

