   244→<script setup lang="ts">
   245→import { ref, computed, watch, nextTick } from 'vue';
   246→import { Icon } from '@iconify/vue';
   247→import FloatingPanel from '@vue/common/FloatingPanel.vue';
   248→import NodeLibrary from './NodeLibrary.vue';
   249→import NodeConfig from './configs/NodeConfig.vue';
   250→import GroupedAccountSelector from '@vue/common/GroupedAccountSelector.vue';
   251→import { useDraftPanelState } from '@composables/useDraftPanelState';
   252→import type { WorkflowNode } from '@appwrite/schemas/workflowNode';
   253→import type { OAuthAccount } from '@appwrite/schemas/oAuthAccount';
   254→import type { Condition } from '@/schemas/workflows/actionSchemas';
   255→import { getDefaultConfig, type ActionOption, ACTION_CATEGORIES } from '@/utils/workflowUtils';
   256→import { serializeWorkflowConfig, deserializeWorkflowConfig } from '@/utils/workflowConfigHelpers';
   257→
   258→interface Props {
   259→  modelValue: boolean;
   260→  workflowId: string;
   261→  oauthAccounts: OAuthAccount[];
   262→  availableNodes: WorkflowNode[];
   263→  triggerAccountId?: string;
   264→  aiCredits?: number;
   265→  initialAction?: string;
   266→  initialAccountId?: string;
   267→  initialConfig?: string;
   268→  initialConditions?: Condition[];
   269→  mode?: 'add-step' | 'add-publish-content' | 'browse-library' | 'edit-node';
   270→  targetNodeId?: string;
   271→}
   272→
   273→interface Emits {
   274→  (e: 'update:modelValue', value: boolean): void;
   275→  (e: 'complete', data: {
   276→    action: string;
   277→    config: string | Record<string, any>;
   278→    accountId?: string;
   279→    conditions?: Condition[];
   280→  }): void;
   281→  (e: 'cancel'): void;
   282→}
   283→
   284→const props = defineProps<Props>();
   285→const emit = defineEmits<Emits>();
   286→
   287→// Floating panel ref
   288→const floatingPanelRef = ref();
   289→
   290→// Step management
   291→interface WorkflowStep {
   292→  id: string;
   293→  title: string;
   294→  subtitle: string;
   295→  required: boolean;
   296→}
   297→
   298→const steps = ref<WorkflowStep[]>([
   299→  {
   300→    id: 'library',
   301→    title: 'Choose Action',
   302→    subtitle: 'Select what you want to do',
   303→    required: true
   304→  },
   305→  {
   306→    id: 'config',
   307→    title: 'Configure',
   308→    subtitle: 'Set up the action parameters',
   309→    required: true
   310→  },
   311→  {
   312→    id: 'account',
   313→    title: 'Select Account',
   314→    subtitle: 'Choose which account to use',
   315→    required: false // Will be set based on action requirements
   316→  }
   317→]);
   318→
   319→const currentStepIndex = ref(0);
   320→const transitionDirection = ref<'forward' | 'backward'>('forward');
   321→const isEditMode = computed(() => props.mode === 'edit-node');
   322→
   323→// Draft panel state - simple local refs, no events
   324→const { action, account, config, conditions: draftConditions, reset, toNode } = useDraftPanelState();
   325→
   326→// Computed for template compatibility
   327→const selectedAction = computed(() => action.value);
   328→const selectedAccountId = computed(() => account.value?.$id);
   329→const selectedAccount = computed(() => account.value);
   330→const conditions = computed({
   331→  get: () => draftConditions.value,
   332→  set: (val) => { draftConditions.value = val; }
   333→});
   334→
   335→// Computed properties
   336→const currentStep = computed(() => steps.value[currentStepIndex.value]);
   337→
   338→const currentStepTitle = computed(() => currentStep.value.title);
   339→const currentStepSubtitle = computed(() => currentStep.value.subtitle);
   340→
   341→const triggerAction = computed(() => {
   342→  // Get trigger action from workflow if available
   343→  return undefined; // TODO: Implement based on workflow context
   344→});
   345→
   346→const canProceedToNext = computed(() => {
   347→  const step = currentStep.value;
   348→
   349→  switch (step.id) {
   350→    case 'library':
   351→      return !!selectedAction.value;
   352→    case 'config':
   353→      // Always allow proceeding from config (even with default config)
   354→      return !!selectedAction.value;
   355→    case 'account':
   356→      // Check if action needs account and if one is selected
   357→      return !actionNeedsAccount.value || !!selectedAccountId.value;
   358→    default:
   359→      return false;
   360→  }
   361→});
   362→
   363→const canComplete = computed(() => {
   364→  return !!selectedAction.value && (!actionNeedsAccount.value || !!selectedAccountId.value);
   365→});
   366→
   367→const actionNeedsAccount = computed(() => {
   368→  if (!selectedAction.value) return false;
   369→
   370→  // Find action definition to check if it needs target
   371→  const allActions = Object.values(ACTION_CATEGORIES).flatMap(category => category.actions);
   372→  const actionDef = allActions.find(action => action.value === selectedAction.value);
   373→
   374→  return actionDef?.needsTarget ?? false;
   375→});
   376→
   377→// Create temp node config for NodeConfig component
   378→const tempNodeConfig = computed(() => {
   379→  if (!action.value) return null;
   380→
   381→  return {
   382→    nodeType: 'ACTION',
   383→    action: action.value,
   384→    config: config.value,
   385→    connections: '{}',
   386→    workflowId: props.workflowId
   387→  } as WorkflowNode;
   388→});
   389→
   390→// Watch for action changes to update step requirements
   391→watch(selectedAction, (newAction) => {
   392→  if (newAction) {
   393→    // Update account step requirement
   394→    const accountStep = steps.value.find(step => step.id === 'account');
   395→    if (accountStep) {
   396→      accountStep.required = actionNeedsAccount.value;
   397→    }
   398→  }
   399→});
   400→
   401→// Step navigation
   402→const goToNextStep = async () => {
   403→  if (!canProceedToNext.value) return;
   404→
   405→  // Skip account step if not required
   406→  if (currentStep.value.id === 'config' && !actionNeedsAccount.value) {
   407→    // Go directly to complete
   408→    await completeWorkflow();
   409→    return;
   410→  }
   411→
   412→  if (currentStepIndex.value < steps.value.length - 1) {
   413→    transitionDirection.value = 'forward';
   414→
   415→    // Trigger step transition animation
   416→    if (floatingPanelRef.value) {
   417→      await floatingPanelRef.value.performStepTransition('forward');
   418→    }
   419→
   420→    currentStepIndex.value++;
   421→  }
   422→};
   423→
   424→const goToPreviousStep = async () => {
   425→  if (currentStepIndex.value > 0) {
   426→    transitionDirection.value = 'backward';
   427→
   428→    // Trigger step transition animation
   429→    if (floatingPanelRef.value) {
   430→      await floatingPanelRef.value.performStepTransition('backward');
   431→    }
   432→
   433→    currentStepIndex.value--;
   434→  }
   435→};
   436→
   437→// Action handlers
   438→const handleSelectTargetAccount = (action: string) => {
   439→  // This is called when an action that needs a target is selected
   440→  // We'll handle this in the account selection step
   441→  console.log('Action needs target account:', action);
   442→};
   443→
   444→const handleUpdateConditions = (newConditions: Condition[]) => {
   445→  draftConditions.value = Array.isArray(newConditions) ? [...newConditions] : [];
   446→};
   447→
   448→const handleProceedToConfig = (actionValue: string) => {
   449→  action.value = actionValue;
   450→  goToNextStep();
   451→};
   452→
   453→const handleConfigUpdate = (newConfig: string) => {
   454→  config.value = newConfig;
   455→};
   456→
   457→const handleAccountSelection = (accountId: string | undefined) => {
   458→  const selectedAcc = props.oauthAccounts.find(a => a.$id === accountId);
   459→  account.value = selectedAcc;
   460→};
   461→
   462→const handleAccountSelect = (selectedAcc: OAuthAccount) => {
   463→  account.value = selectedAcc;
   464→};
   465→
   466→const handleAccountConfirm = (selectedAcc: OAuthAccount | undefined) => {
   467→  if (selectedAcc) {
   468→    account.value = selectedAcc;
   469→    // Auto-proceed to completion if we have everything
   470→    if (canComplete.value) {
   471→      completeWorkflow();
   472→    }
   473→  }
   474→};
   475→
   476→const completeWorkflow = async () => {
   477→  if (!action.value) return;
   478→
   479→  try {
   480→    const nodeData = toNode();
   481→
   482→    // For actions like publish_content, the accountId might be in the config instead of account.value
   483→    // Try to extract it from the config if not already set
   484→    let finalAccountId = nodeData.targetAccountId;
   485→    if (!finalAccountId && nodeData.config) {
   486→      try {
   487→        const parsedConfig = typeof nodeData.config === 'string' ? JSON.parse(nodeData.config) : nodeData.config;
   488→        if (parsedConfig.accountId) {
   489→          finalAccountId = parsedConfig.accountId;
   490→        }
   491→      } catch (e) {
   492→        console.warn('Failed to parse config for accountId extraction', e);
   493→      }
   494→    }
   495→
   496→    // Show success animation
   497→    if (floatingPanelRef.value) {
   498→      await floatingPanelRef.value.showSuccessAnimation();
   499→    }
   500→
   501→    // Emit completion
   502→    emit('complete', {
   503→      action: nodeData.action,
   504→      config: nodeData.config,
   505→      accountId: finalAccountId,
   506→      conditions: nodeData.conditions
   507→    });
   508→
   509→    // Reset draft state
   510→    reset();
   511→    draftConditions.value = [];
   512→
   513→    // Close panel
   514→    setTimeout(() => {
   515→      emit('update:modelValue', false);
   516→    }, 500);
   517→
   518→  } catch (error) {
   519→    console.error('Failed to complete workflow:', error);
   520→    if (floatingPanelRef.value) {
   521→      await floatingPanelRef.value.showErrorAnimation();
   522→    }
   523→  }
   524→};
   525→
   526→const handleSaveEdit = async () => {
   527→  if (!canComplete.value) return;
   528→  await completeWorkflow();
   529→};
   530→
   531→const handleClose = () => {
   532→  reset();
   533→  draftConditions.value = [];
   534→  currentStepIndex.value = 0;
   535→  transitionDirection.value = 'forward';
   536→  emit('cancel');
   537→  emit('update:modelValue', false);
   538→};
   539→
   540→// Utility functions
   541→const getActionLabel = (action: string | undefined): string => {
   542→  if (!action) return 'Unknown Action';
   543→
   544→  const allActions = Object.values(ACTION_CATEGORIES).flatMap(category => category.actions);
   545→  const actionDef = allActions.find(a => a.value === action);
   546→
   547→  return actionDef?.label || action.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
   548→};
   549→
   550→const getAllowedPlatforms = (action: string | undefined): string[] => {
   551→  if (!action) return [];
   552→
   553→  // TODO: Implement platform restrictions based on action
   554→  // For now, return all platforms
   555→  return [];
   556→};
   557→
   558→// Animation handlers
   559→const onStepEnter = (el: Element) => {
   560→  const element = el as HTMLElement;
   561→  element.classList.add('step-entering');
   562→};
   563→
   564→const onStepAfterEnter = (el: Element) => {
   565→  const element = el as HTMLElement;
   566→  element.classList.remove('step-entering');
   567→};
   568→
   569→const onStepLeave = (el: Element) => {
   570→  const element = el as HTMLElement;
   571→  element.classList.add('step-leaving');
   572→};
   573→
   574→const onAnimationEnd = (type: string) => {
   575→  console.log('Animation completed:', type);
   576→};
